mapper.insert:
INSERT INTO		BMS_FCLT_PARAM_INFO	
				(
					NODE_ID
					,FCLT_ID
					,FCLT_KIND
					,PARAM_DIV
					,PARAM_KIND
					,MNG_ID
					,DATA_TYPE
					,DATA_LENGTH
					,DATA_VAL
					,ATTR1_VAL
					,ATTR2_VAL
					,SCHEDULE_YN
					,USE_YN
					,AUTO_YN
					,REMARK
					,UPD_DTM
					,UPD_ID						
				)
VALUES			(
					#{NODE_ID}
					,#{FCLT_ID}
					,#{FCLT_KIND}
					,#{PARAM_DIV}
					,#{PARAM_KIND}
					,#{MNG_ID}
					,#{DATA_TYPE}
					,NULLIF(#{DATA_LENGTH}, '')
					,#{DATA_VAL}
					,#{ATTR1_VAL}
					,#{ATTR2_VAL}
					,#{SCHEDULE_YN}
					,#{USE_YN}
					,#{AUTO_YN}
					,#{REMARK}
					,#{UPD_DTM}
					,#{SSN_UPD_ID}				
				);


INSERT INTO		BMS_FCLT_COND_PARAM_INFO	
				(
					NODE_ID
					,FCLT_ID
					,FCLT_KIND
					,PARAM_DIV
					,PARAM_KIND
					,MNG_ID
					,DATA_VAL
					,UPD_DTM
				)
VALUES			(
					#{NODE_ID}
					,#{FCLT_ID}
					,#{FCLT_KIND}
					,#{PARAM_DIV}
					,#{PARAM_KIND}
					,#{MNG_ID}
					,#{DATA_VAL}
					,#{UPD_DTM}
				);

mapper.update:
UPDATE			BMS_FCLT_PARAM_INFO
SET				NODE_ID = #{NODE_ID}
				,FCLT_ID = #{FCLT_ID}
				,FCLT_KIND = #{FCLT_KIND}
				,PARAM_DIV = #{PARAM_DIV}
				,PARAM_KIND = #{PARAM_KIND}
				,MNG_ID = #{MNG_ID}
				,DATA_TYPE = #{DATA_TYPE}
				,DATA_LENGTH = NULLIF(#{DATA_LENGTH}, '')
				,DATA_VAL = #{DATA_VAL}
				,ATTR1_VAL = #{ATTR1_VAL}
				,ATTR2_VAL = #{ATTR2_VAL}
				,SCHEDULE_YN = #{SCHEDULE_YN}
				,USE_YN = #{USE_YN}
				,AUTO_YN = #{AUTO_YN}
				,REMARK = #{REMARK}
				,UPD_DTM = #{UPD_DTM}
				,UPD_ID = #{SSN_UPD_ID}

WHERE			NODE_ID = #{NODE_ID} 
				AND FCLT_ID = #{FCLT_ID} 
				AND FCLT_KIND = #{OLD_FCLT_KIND} 
				AND PARAM_DIV = #{OLD_PARAM_DIV} 
				AND PARAM_KIND = #{OLD_PARAM_KIND};


UPDATE			BMS_FCLT_COND_PARAM_INFO
SET				NODE_ID = #{NODE_ID}
				,FCLT_ID = #{FCLT_ID}
				,FCLT_KIND = #{FCLT_KIND}
				,PARAM_DIV = #{PARAM_DIV}
				,PARAM_KIND = #{PARAM_KIND}
				,MNG_ID = #{MNG_ID}
				,DATA_VAL = #{DATA_VAL}
				,UPD_DTM = #{UPD_DTM}

WHERE			NODE_ID = #{NODE_ID} 
				AND FCLT_ID = #{FCLT_ID} 
				AND FCLT_KIND = #{OLD_FCLT_KIND} 
				AND PARAM_DIV = #{OLD_PARAM_DIV} 
				AND PARAM_KIND = #{OLD_PARAM_KIND};

mapper.delete:
DELETE FROM 	BMS_FCLT_PARAM_INFO
WHERE			NODE_ID = #{NODE_ID} AND FCLT_ID = #{FCLT_ID} AND FCLT_KIND = #{FCLT_KIND} AND PARAM_DIV = #{PARAM_DIV} AND PARAM_KIND = #{PARAM_KIND};

DELETE FROM 	BMS_FCLT_COND_PARAM_INFO
WHERE			NODE_ID = #{NODE_ID} AND FCLT_ID = #{FCLT_ID} AND FCLT_KIND = #{FCLT_KIND} AND PARAM_DIV = #{PARAM_DIV} AND PARAM_KIND = #{PARAM_KIND};
