mapper.insert:
INSERT INTO		 BMS_PARTNER_MST
			(
				PARTNER_ID
			,	PARTNER_DIV

			,	SORT

			,	PARTNER_NM
			,	PARTNER_ENM
			,	REP_NM
			,	COMP_REG_NO
			,	ADDR
			,	PHONE
			,	FAX
			,	EMAIL
			,	REMARK
			,	UPD_DTM
			,	UPD_ID
				)
VALUES
			(
				#{PARTNER_ID}
			,	#{PARTNER_DIV}

			,	#{SORT}

			,	#{PARTNER_NM}
			,	#{PARTNER_ENM}
			,	#{REP_NM}
			,	AES_ENCRYPT(#{COMP_REG_NO}, HEX(SHA2('TRACOM', 512)))
			,	#{ADDR}
			,	AES_ENCRYPT(#{PHONE}, HEX(SHA2('TRACOM', 512)))
			,	AES_ENCRYPT(#{FAX}, HEX(SHA2('TRACOM', 512)))
			,	AES_ENCRYPT(#{EMAIL}, HEX(SHA2('TRACOM', 512)))
			,	#{REMARK}
			,	#{UPD_DTM}
			,	#{SSN_USER_ID}
				)

mapper.update:
UPDATE			BMS_PARTNER_MST
SET

				PARTNER_DIV = #{PARTNER_DIV}

			,	SORT = #{SORT}

			,	PARTNER_NM = #{PARTNER_NM}
			,	PARTNER_ENM = #{PARTNER_ENM}
			,	REP_NM = #{REP_NM}
			,	COMP_REG_NO = AES_ENCRYPT(#{COMP_REG_NO}, HEX(SHA2('TRACOM', 512)))
			,	ADDR = #{ADDR}
			,	PHONE = AES_ENCRYPT(#{PHONE}, HEX(SHA2('TRACOM', 512)))
			,	FAX = AES_ENCRYPT(#{FAX}, HEX(SHA2('TRACOM', 512)))
			,	EMAIL = AES_ENCRYPT(#{EMAIL}, HEX(SHA2('TRACOM', 512)))
			,	REMARK = #{REMARK}
			,	UPD_DTM = #{UPD_DTM}
			,	UPD_ID = #{SSN_USER_ID}

WHERE			PARTNER_ID = #{PARTNER_ID}
