mapper.update:
UPDATE 
	BMS_FCLT_PARAM_CFG_INFO 
SET 
	FCLT_KIND = #{FCLT_KIND}
	,PARAM_DIV = #{PARAM_DIV}
	,PARAM_KIND = #{PARAM_KIND}
	,DATA_TYPE = #{DATA_TYPE}
	,DATA_LENGTH = NULLIF(#{DATA_LENGTH}, '')
	,DATA_VAL = #{DATA_VAL}
	,ATTR1_VAL = #{ATTR1_VAL}
	,ATTR2_VAL = #{ATTR2_VAL}
	,SCHEDULE_YN = #{SCHEDULE_YN}
	,USE_YN = #{USE_YN}
	,AUTO_YN = #{AUTO_YN}
	,REMARK = #{REMARK}
	,UPD_DTM = #{UPD_DTM}
	,UPD_ID = #{SSN_USER_ID}
WHERE 
	FCLT_KIND = #{OLD_FCLT_KIND} 
	AND PARAM_DIV = #{OLD_PARAM_DIV} 
	AND PARAM_KIND = #{OLD_PARAM_KIND}

UPDATE 
	BMS_FCLT_SCH_INFO 
SET 
	FCLT_KIND = #{FCLT_KIND}
	,PARAM_DIV = #{PARAM_DIV}
	,PARAM_KIND = #{PARAM_KIND}
	,ST_TIME = #{ST_TIME}
	,ED_TIME = #{ED_TIME}
	,DATA_VAL = #{DATA_VAL}
	,REMARK = #{REMARK}
	,UPD_DTM = #{UPD_DTM}
	,UPD_ID = #{SSN_USER_ID}
WHERE 
	FCLT_KIND = #{FCLT_KIND} AND PARAM_DIV = #{PARAM_DIV} AND PARAM_KIND = #{PARAM_KIND} AND SN = #{SN}
