mapper.update:
UPDATE			BMS_DVC_PARAM_INFO
SET				VHC_ID = #{VHC_ID}
				,DVC_ID = #{DVC_ID}
				,DVC_KIND = #{DVC_KIND}
				,PARAM_DIV = #{PARAM_DIV}
				,PARAM_KIND = #{PARAM_KIND}
				,DATA_TYPE = #{DATA_TYPE}
				,DATA_LENGTH = #{DATA_LENGTH}
				,DATA_VAL = #{DATA_VAL}
				,ATTR1_VAL = #{ATTR1_VAL}
				,ATTR2_VAL = #{ATTR2_VAL}
				,SCHEDULE_YN = #{SCHEDULE_YN}
				,USE_YN = #{USE_YN}
				,AUTO_YN = #{AUTO_YN}
				,REMARK = #{REMARK}
				,UPD_DTM = #{UPD_DTM}
				,UPD_ID = #{SSN_UPD_ID}

WHERE			VHC_ID = #{VHC_ID} AND DVC_ID = #{DVC_ID}

mapper.delete:
DELETE FROM PLF_IMP_DEV_MST
WHERE IMP_ID = #{MNG_ID};

DELETE FROM PLF_IMP_SESS_INFO
WHERE SESS_ID = #{MNG_ID};
