mapper.select:
SELECT DATE_FORMAT(A.OCR_DTM, '%Y-%m-%d %h:%i:%s') OCR_DTM ,A.REP_ROUT_ID ,A.ROUT_ID ,A.VHC_NO ,A.ALLOC_NO ,A.DOOR_SN ,A.LOC_STOP_DIV ,B.STTN_NM ,C.REP_ROUT_NM ,C.ROUT_NM FROM BRT_ACRT_LOC_STOP_LOG A LEFT JOIN BMS_STTN_MST B ON A.STTN_ID = B.STTN_ID AND B.DEL_YN!='Y' LEFT JOIN BMS_ROUT_MST C ON A.ROUT_ID = C.ROUT_ID
AND A.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
AND (A.VHC_NO LIKE CONCAT('%', #{CONTENT3}, '%') || A.VHC_ID LIKE CONCAT('%', #{CONTENT3}, '%'))
AND DATE_FORMAT(A.OCR_DTM, '%Y%m%d') >= #{F_DATE}
AND DATE_FORMAT(A.OCR_DTM, '%Y%m%d') <= #{L_DATE}
AND DATE_FORMAT(A.OCR_DTM, '%Y%m%d') BETWEEN #{F_DATE} AND #{L_DATE}
GROUP BY A.OCR_DTM, A.VHC_ID
GROUP BY A.OCR_DTM, A.STTN_ID
