mapper.select:
SELECT DATE_FORMAT(REQ_DT, '%Y-%m-%d') REQ_DT , SEQ , DATE_FORMAT(REQ_DTM, '%Y-%m-%d %H:%i:%s' ) REQ_DTM , REQ_END_POINT , REQ_QUERY , REQ_IP , RESULT_CD , RESULT_DTL_CD
FROM BMS_API_CALL_HIS
AND REQ_IP LIKE CONCAT('%', #{CONTENT1}, '%')
AND RESULT_CD = #{TYPE}
AND DATE_FORMAT(REQ_DTM, '%Y%m%d') >= #{F_DATE}
AND DATE_FORMAT(REQ_DTM, '%Y%m%d') <= #{L_DATE}
AND DATE_FORMAT(REQ_DTM, '%Y%m%d') BETWEEN #{F_DATE} AND #{L_DATE}
ORDER BY REQ_DTM DESC, SEQ DESC
