mapper.select:
SELECT DATE_FORMAT(A.UPD_DTM, '%Y-%m-%d %H:%m:%s') UPD_DTM , A.NODE_ID , A.REP_ROUT_ID , A.ROUT_ID , A.VHC_NO , A.CTRL_LV , A.STOP_SEC , A.CTRL_TYPE , A.CTRL_PHASE_NO , DATE_FORMAT(A.OCR_DTM, '%Y-%m-%d %H:%m:%s') OCR_DTM , A.OCR_DTM , B.CRS_NM , C.REP_ROUT_NM , D.ROUT_NM
FROM BMS_CRS_SIGOPER_EVENT_HIS A
LEFT JOIN BMS_CRS_MST B ON A.NODE_ID = B.CRS_ID LEFT JOIN BMS_REP_ROUT_MST C ON A.REP_ROUT_ID = C.REP_ROUT_ID LEFT JOIN BMS_ROUT_MST D ON A.ROUT_ID = D.ROUT_ID
AND A.REP_ROUT_ID = #{TYPE1}
AND B.CRS_NM LIKE CONCAT('%', #{CONTENT1}, '%')
AND DATE_FORMAT(A.OCR_DTM, '%Y%m%d') >= #{F_DATE}
AND DATE_FORMAT(A.OCR_DTM, '%Y%m%d') <= #{L_DATE}
AND DATE_FORMAT(A.OCR_DTM, '%Y%m%d') BETWEEN #{F_DATE} AND #{L_DATE}
ORDER BY UPD_DTM DESC
