head.script:
	scwin.onpageload = function() {

		scwin.param = com.getParameter();
		scwin.closeCallbackFncName = scwin.param.callbackFn;
		com.setEnterKeyEvent(popup_header, scwin.btn_search_onclick);

		//dlt_BRT_OPER_ALLOC_PL_NODE_INFO.setJSON(scwin.param.data5, true);
		dlt_BRT_OPER_ALLOC_RL_NODE_INFO.setJSON(scwin.param.data6, true);
		dma_sub_search.set("OPER_DT", scwin.param.operDt);
		popup_title.setValue(scwin.param.data3 + " " + scwin.param.data4 + "회차");

		scwin.drawCompChart();		
	};

	scwin.onpageunload = function() {

	};



	/*scwin.sub_AL0203G0P0_submitdone = function(e) {

