	function date_ascending(a, b) {
       return a - b;
    };


	var CharData = function(){
		this.seriesname = {};
		this.fill = {};
		this.backgroundColor = {};
		this.borderColor = {};
		this.data = [];
		this.borderWidth = {}; //선 width
		this.pointBorderWidth = {}; //포인터 width
		this.tension = {};	//곡선 or 직선
		this.pointRadius = {};	//포인트 반지름 0일 경우 포인트 안그려짐
	}

	scwin.drawCompChart = function(){

		var datasets = new Array();
		var transData = new Array();
		var i = 0;
		var j = 0;
		var num = 0;

		var rowCount = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getRowCount();
		var jsonData = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getAllJSON();
		var rlAllJson = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getAllJSON();

		//운행계획
		var data = new Array();
		for(var y = 0; y < jsonData.length; y++){
			var label = jsonData[y].OPER_SN + "회차";
			//var updDtm = jsonData[y].UPD_DTM;
			var operSn = jsonData[y].OPER_SN;

			if(jsonData[y].ARRV_TM.substring(0,2) == '24' || jsonData[y].ARRV_TM.substring(0,2) == '25' || jsonData[y].ARRV_TM.substring(0,2) == '26'){

				var nextDay = new Date(com.transDate(dma_sub_search.get("OPER_DT"), 'dash'));
				nextDay.setDate(nextDay.getDate() +1).toLocaleString();
				var day = nextDay.getDate().toString();
				var year = nextDay.getFullYear().toString();
				var month = nextDay.getMonth()+1;
				if(month.toString().length == '1'){
					month = '0'+month
				}
				var newNextDate = year+'-'+month+'-'+day
				var updDtm = newNextDate;

				if(jsonData[y].ARRV_TM.substring(0,2) == '24'){
					var jsonArrvTm = jsonData[y].ARRV_TM.replace("24", "00");
					var jsonDprtTm = jsonData[y].DPRT_TM.replace("24", "00");
				}else if(jsonData[y].ARRV_TM.substring(0,2) == '25'){
					var jsonArrvTm = jsonData[y].ARRV_TM.replace("25", "01");
					var jsonDprtTm = jsonData[y].DPRT_TM.replace("25", "01");
				}else if(jsonData[y].ARRV_TM.substring(0,2) == '26'){
					var jsonArrvTm = jsonData[y].ARRV_TM.replace("26", "02");
					var jsonDprtTm = jsonData[y].DPRT_TM.replace("26", "02");
				}

				var arrvTm = newNextDate+' '+jsonArrvTm
				var dprtTm = newNextDate+' '+jsonDprtTm

			}else{
				var updDtm = com.transDate(dma_sub_search.get("OPER_DT"), 'dash');
				var arrvTm = com.transDate(dma_sub_search.get("OPER_DT"), 'dash')+' '+jsonData[y].ARRV_TM;
				var dprtTm = com.transDate(dma_sub_search.get("OPER_DT"), 'dash')+' '+jsonData[y].DPRT_TM;
			}

			var accruLen = jsonData[y].ACCRU_LEN;
			var nodeType = jsonData[y].NODE_TYPE;

			//if(nodeType=='NT002'){
				var categoryJson = new Object();

				categoryJson.x = new Date(arrvTm);
				categoryJson.y = accruLen;
				categoryJson.NODE_NM = jsonData[y].NODE_NM;
				data.push(categoryJson);

			//}

			//if((nodeType=='NT002')&&(arrvTm!=dprtTm)){
				var categoryJson = new Object();

				categoryJson.x = new Date(dprtTm);
				categoryJson.y = accruLen;
				categoryJson.NODE_NM = jsonData[y].NODE_NM;
				data.push(categoryJson);
			//}

		};

		var charData = new CharData();

		charData.data = data;
		//charData.label = label+" 운행계획";
		charData.label = "운행계획";

		charData.backgroundColor = 'rgba(0, 0, 255, 0.5)';
		charData.borderColor = 'rgba(0, 0, 255, 0.5)';
		charData.borderWidth = 1;
		charData.pointBorderWidth = 1;
		charData.tension = 0;
		charData.pointRadius = 1;

		datasets.push(charData);


		//실제운행
		var data = new Array();
		for(var y = 0; y < rlAllJson.length; y++){
			var label = rlAllJson[y].OPER_SN + "회차";
			//var updDtm = rlAllJson[y].UPD_DTM;
			var operSn = rlAllJson[y].OPER_SN;

			if(rlAllJson[y].REAL_ARRV_TM.substring(0,2) == '24' || rlAllJson[y].REAL_ARRV_TM.substring(0,2) == '25' || rlAllJson[y].REAL_ARRV_TM.substring(0,2) == '26'){

				var nextDay = new Date(com.transDate(dma_sub_search.get("OPER_DT"), 'dash'));
				nextDay.setDate(nextDay.getDate() +1).toLocaleString();
				var day = nextDay.getDate().toString();
				var year = nextDay.getFullYear().toString();
				var month = nextDay.getMonth()+1;
				if(month.toString().length == '1'){
					month = '0'+month
				}
				var newNextDate = year+'-'+month+'-'+day
				var updDtm = newNextDate;

				if(rlAllJson[y].REAL_ARRV_TM.substring(0,2) == '24'){
					var jsonArrvTm = rlAllJson[y].REAL_ARRV_TM.replace("24", "00");
					var jsonDprtTm = rlAllJson[y].REAL_DPRT_TM.replace("24", "00");
				}else if(rlAllJson[y].REAL_ARRV_TM.substring(0,2) == '25'){
					var jsonArrvTm = rlAllJson[y].REAL_ARRV_TM.replace("25", "01");
					var jsonDprtTm = rlAllJson[y].REAL_DPRT_TM.replace("25", "01");
				}else if(rlAllJson[y].REAL_ARRV_TM.substring(0,2) == '26'){
					var jsonArrvTm = rlAllJson[y].REAL_ARRV_TM.replace("26", "02");
					var jsonDprtTm = rlAllJson[y].REAL_DPRT_TM.replace("26", "02");
				}

				var arrvTm = newNextDate+' '+jsonArrvTm
				var dprtTm = newNextDate+' '+jsonDprtTm

			}else{
				var updDtm = com.transDate(dma_sub_search.get("OPER_DT"), 'dash');
				var arrvTm = com.transDate(dma_sub_search.get("OPER_DT"), 'dash')+' '+rlAllJson[y].REAL_ARRV_TM;
				var dprtTm = com.transDate(dma_sub_search.get("OPER_DT"), 'dash')+' '+rlAllJson[y].REAL_DPRT_TM;
			}

			var accruLen = rlAllJson[y].ACCRU_LEN;
			var nodeType = rlAllJson[y].NODE_TYPE;

			if((com.isEmpty(rlAllJson[y].REAL_ARRV_TM)==false)){
				var categoryJson = new Object();

				categoryJson.x = new Date(arrvTm);
				categoryJson.y = accruLen;
				categoryJson.NODE_NM = jsonData[y].NODE_NM;
				data.push(categoryJson);

			}

			if((arrvTm!=dprtTm)&&(com.isEmpty(rlAllJson[y].REAL_DPRT_TM)==false)){
				var categoryJson = new Object();

				categoryJson.x = new Date(dprtTm);
				categoryJson.y = accruLen;
				categoryJson.NODE_NM = jsonData[y].NODE_NM;
				data.push(categoryJson);
			}

		};

		var charData = new CharData();

		charData.data = data;
		//charData.label = label+" 실제운행";
		charData.label = "실제운행";

		charData.backgroundColor = 'rgba(255, 99, 132, 1)';
		charData.borderColor = 'rgba(255, 99, 132, 1)';
		charData.borderWidth = 1;
		charData.pointBorderWidth = 1;
		charData.tension = 0;
		charData.pointRadius = 1;

		if(charData.data.length > 0){
			datasets.push(charData);
		}
		//datasets.push(charData);

		var ctx = document.getElementById('myChart');
		var myChart = new Chart(ctx, {
			    type: 'line',
				data: {
					datasets
				    /*datasets: [{
					      label: '운행계획',
					      data: [{
					        x: new Date(2021, 10, 19, 03, 24, 20),
					        y: 1
					      }, {
					        x: new Date(2021, 10, 19, 4, 24, 0),
					        y: 10
					      }
					      ]
					    },
					    {
					      label: '실제운행',
					      data: [{
					        x: new Date(2021, 10, 19, 3, 28, 0),
					        y: 2
					      }, {
					        x: new Date(2021, 10, 19, 4, 24, 0),
					        y: 11
					      }]
					    }
					    ]*/
				  },
			    options: {
			        scales: {
			           xAxes: [{
				        type: 'time',
				        time: {
				          unit: 'minute',
				          unitStepSize: 3 //x축 시간 간격 (분)
				        }
				      }]
			        },
			        tooltips:{
			        	callbacks:{
			        		label: function(tooltipItem, data){
								var index = tooltipItem.index
								var dataSetIndex = tooltipItem.datasetIndex
			        			return '노드명' + " : " + data.datasets[dataSetIndex].data[index].NODE_NM;
			        		},
			        	footer: function(tooltipItem, data){

			        			return '거리(m) : ' + tooltipItem[0].yLabel
			        		}
			        	}
			        }

			    }
			});
	}
