var autoOptions = [];

var checkBoxClick = false;

scwin.onpageload = function() {
	autoOptions = {
		Main : { grid : FM0204G0, srchSbm : sub_FM0204G0R0, srchGrp : pgm_header, keyColumn : "NODE_ID", focusColumn : "NODE_NM", name :  "차량 목록"},
		Sub1 : { grid : FM0204G1, srchSbm : sub_FM0204G1R0, name :  "장치 목록"}
	 };

	var mainOptions = {
		SAVE:{cbFnc:scwin.selectReset,nm:"일괄리셋", class:"reset"}
		,ADD:{}
		,DEL:{}
		,CNL:{}

	}

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions, mainOptions);

	var subOptions2 = {
		SAVE:[{cbFnc:scwin.selectControl,nm:"전송", class:"send"}]
	};

	com.setSubBtn2(wfm_subBtn2, autoOptions, subOptions2);

	var subOptions3 = {
		SAVE:[{cbFnc:scwin.selectControl2,nm:"전송", class:"send"}]
	};

	com.setSubBtn2(wfm_subBtn3, autoOptions, subOptions3);

	var codeOptions = [ {code : "FCLT_KIND", compID : "FM0204G0:FCLT_KIND"},
						{code : "PARAM_KIND"},
						{code : "FCLT_COND", compID : "FM0204G0:FCLT_COND"}
	 ];

	com.setCommonDtl(codeOptions, function() {
		com.searchGrid(FM0204G0, sub_FM0204G0R0);
	});

	com.executeSubmission(sub_nodeItem);	

	$("#FM0204_TAB").attr("style", "width:200px; height:26px; margin-left: 10px; float:left");
	//$("#mf_tac_layout_contents_MN00060204_body_pgm_content3").css("display", "none");
	pgm_content3.hide();
	FM0204_ul.hide();

	//탭 목록 클릭 이벤트
	$(".ctl_fclt").find("li").click( function() {
		var li_id = this.id;
		$(".ctl_fclt").find("li").removeClass("on");
		$(".ctl_fclt").find("#"+li_id).addClass("on");

		if(li_id == "li_bit") {
			//$(".pgm_list").css("display", "");
			//$("#mf_tac_layout_contents_MN00060204_body_pgm_content3").css("display", "none");
			pgm_list.show("");
			pgm_content3.hide();
			pgm_list2.hide();
		}
		else if(li_id == "li_airtube") {
			$("#FM0204_TAB2").attr("style", "width:200px; height:26px; margin-left: 10px; float:left");
			//$(".pgm_list").css("display", "none");
			//$("#mf_tac_layout_contents_MN00060204_body_pgm_content3").css("display", "");
			pgm_list.hide();
			pgm_content3.show("");
			pgm_list2.show("");
			com.executeSubmission(sub_FM0204G2R0);
		}
	});

};

