//PARAM_KIND헤더 동적으로 가져오는 서브미션 콜백 메소드
scwin.sub_FM0204G0R1_submitdone = function(e) {
	scwin.setGridViewInfo();

	var codeOptions = [ {code : "FCLT_COND", compID : "FM0204G0:FCLT_COND"}];
	com.setCommonDtl(codeOptions);

	com.setFocusedCell(FM0204G0, "NODE_ID");
	dlt_BMS_FCLT_COND_PARAM_INFO.setCellData(0, 'chk', '1');

	for (i=0; i<dlt_BMS_FCLT_COND_PARAM_INFO.getDataLength(); i++) {
		//비정상
		if (dlt_BMS_FCLT_COND_PARAM_INFO.getCellData(i, "FCLT_COND") == "FC021") {
			FM0204G0.setCellClass(i, "FCLT_COND", "cond_abnormal");
		}
		//정상
		else if (dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COND") == "FC020") {
			FM0204G0.setCellClass(i, "FCLT_COND", "cond_normal");
		}
	}
};

//동적 그리드 함수
scwin.setGridViewInfo = function() {
	var gridHeader ="", gridBody="";
	var rowData = dlt_BMS_FCLT_COND_PARAM_INFO.getAllJSON();
	var paramKindJson = dlt_PARAM_KIND.getAllJSON();
	var rowCntParamKind = dlt_PARAM_KIND.getRowCount();
	//var rowCntArr = dlt_BMS_FCLT_COND_PARAM_INFO.getRowCount();
	//var rowCntParamKind = dlt_commonDtlPARAM_KIND.getRowCount();

	//헤더 시작
	gridHeader +='<w2:column width="40" inputType="checkbox" style="height:20px" id="chkHeader" value="선택" blockSelect="false" sortable="false"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm_1" value="정류소ID" blockSelect="false" displayMode="label" sortable="true"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="90" inputType="text" style="height:20px" id="h_colunm_2" value="정류소명" blockSelect="false" displayMode="label"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm_3" value="시설물ID" blockSelect="false" displayMode="label" sortable="true"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="170" inputType="text" style="height:20px" id="h_colunm_4" value="시설물종류" blockSelect="false" displayMode="label"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm_5" value="시설물상태" blockSelect="false" displayMode="label"></w2:column>';

	//동적 헤더 시작
	for(var i = 0; i < rowCntParamKind; i++) {
		var paramKind = dlt_PARAM_KIND.getCellData(i, "PARAM_KIND");

		if(paramKind != '' && paramKind != 'PK001'){
			var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
			var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");

			gridHeader +='<w2:column removeBorderStyle="false" width="110" inputType="text" style="height:20px" id="h_colunm'+i+3+'" value="'+dlCdNm+'" blockSelect="false" displayMode="label"></w2:column>';
			gridHeader +='<w2:column removeBorderStyle="false" width="110" inputType="text" style="height:20px" id="h_colunm'+i+4+'" value="'+dlCdNm+'자동'+'" blockSelect="false" displayMode="label"></w2:column>';
		}

	}

	//바디 시작
	gridBody += '<w2:column blockSelect="false" disabled="false" falseValue="0" defaultValue="" trueValue="1" id="chk" readOnly="false" style="height:20px" inputType="checkbox" width="39"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="NODE_ID" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="NODE_NM" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="FCLT_ID" value="" blockSelect="false" displayMode="label"></w2:column>';
	//gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="drilldown" style="height:20px" id="FCLT_KIND_NM" value="" blockSelect="false" displayMode="label" showDepth="2" depthColumn="LEVEL" depthType="div" textAlign="left"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="text" style="height:20px" id="FCLT_KIND_NM" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="select" style="height:20px" id="FCLT_COND" value="" blockSelect="false" displayMode="label"></w2:column>';

	//동적 바디 시작
	for(var i = 0; i < rowCntParamKind; i++) {
		var paramKind = dlt_PARAM_KIND.getCellData(i, "PARAM_KIND");
		var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
		var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");
		var displayForMatter = '';

		if(dlCd == 'PK002' || dlCd == 'PK003' || dlCd == 'PK006' || dlCd == 'PK007'){
			displayForMatter = 'scwin.onOffDisFormatter'; //데이터유형이 ON/OFF인 경우 displayformaatter 추가
		}

		if(paramKind != '' && paramKind != 'PK001'){
			gridBody +='<w2:column removeBorderStyle="false" width="113" inputType="text" style="height:20px" id="'+paramKind+'" value="" blockSelect="false" displayMode="label" displayFormatter="'+displayForMatter+'"></w2:column>';
			gridBody +='<w2:column removeBorderStyle="false" width="113" inputType="text" style="height:20px" id="'+paramKind+'_AUTO_YN'+'" value="" blockSelect="false" displayMode="label" displayFormatter="scwin.AutoDisFormatter"></w2:column>';
		}
	}

	//gridView 정보 정의 
	//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
	var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" '
						+'autoFit="lastColumn" class="cm_grid left" dataList="data:dlt_BMS_FCLT_COND_PARAM_INFO" ev:oncellclick="scwin.FM0204G0_oncellclick" '
						+'ev:onrowindexchange="scwin.FM0204G0_onrowindexchange" fixedColumnWithHidden="true" id="FM0204G0" ignoreCellClick="false" rowNumHeaderValue="순번" rowNumVisible="true" '
						+'rowStatusVisible="false" rowStatusWidth="40" scrollByColumn="false" style="" useShiftKey="true" autoFitMinWidth="0" readOnly="true" '
						+'sortable="false" focusMode="row" showSortableImage="true" sortEvent="onclick" ev:onheaderclick="scwin.FM0204G0_onheaderclick">';
	gridStr += '<w2:header style="" id="header2">'
	+ '<w2:row style="" id="row3">'
	+ gridHeader
	+'<w2:column removeBorderStyle="false" width="170" inputType="text" style="height:20px" id="h_colunm_5" value="" blockSelect="false" displayMode="label"></w2:column>'
	+'</w2:row>'
	+'</w2:header>'
	+'<w2:gBody style="" id="gBody2">'
	+'<w2:row id="row2" style="">'
	+ gridBody
	+'<w2:column removeBorderStyle="false" width="117" inputType="text" style="height:20px" id="" value="" blockSelect="false" displayMode="label"></w2:column>'
	+'</w2:row>'
	+'</w2:gBody>';
	gridStr += '<w2:footer id="footer1" style=""><w2:row id="row3" style=""><w2:column blockSelect="false" colSpan="30" displayMode="label" expression="&quot;데이터 개수 : &quot; + COUNT(\'COMP_ID\')" id="column42" inputType="expression" removeBorderStyle="false" style="height:20px;" textAlign="left" width="2386"></w2:column></w2:row></w2:footer>';
	gridStr += '</w2:gridView>';

	FM0204G0.setGridStyle(WebSquare.xml.parse(gridStr , true ));
	//dlt_BMS_FCLT_COND_PARAM_INFO.removeAll();
	autoOptions.Main.grid = FM0204G0;

};

//FM0204G0그리드 row 변경 이벤트
scwin.FM0204G0_onrowindexchange = function(row,oldRow) {

	if (checkBoxClick == false) {
		dma_subsearch.set("MNG_ID", dlt_BMS_FCLT_COND_PARAM_INFO.getCellData(row, "MNG_ID"));
		var focusOption = {keyMap : dma_subsearch};
		com.changeDualGrid(FM0204G0, FM0204G1, null, sub_FM0204G1R0, focusOption, row, oldRow);
	}else {
		checkBoxClick = false;	
	}

	dma_subsearch.set("FCLT_ID", dlt_BMS_FCLT_COND_PARAM_INFO.getCellData(row, "FCLT_ID"));
	com.executeSubmission(sub_FM0204G0R2);

};

//제어 조회 서브미션 콜백 메소드
scwin.sub_FM0204G0R2_submitdone = function(e) {
	var paramKind = dlt_CTL_PARAM_KIND.getColData("PARAM_KIND");
	var autoYn = dlt_CTL_PARAM_KIND.getColData("AUTO_YN");

	FM0204F0.setDisabled(true);

	//버튼 투명도 설정
	T0_PK009_DIS.setOpacity(0.45);
	T0_PK009_CAP.setOpacity(0.45);
	T0_PK010_DIS.setOpacity(0.45);
	T0_PK010_REC.setOpacity(0.45);

	//후에 제어가 추가 된다면 추가작업 필요.
	for(var i = 0; i < paramKind.length; i++){
		switch (paramKind[i]) {
		case 'PK001':
			T0_PK001.setDisabled(false);
			break;
		case 'PK002':
			T0_PK002.setDisabled(false);
			break;
