			T0_PK009_DIS.setDisabled(false);
			T0_PK009_CAP.setDisabled(false);
			T0_PK009_DIS.setOpacity(1);
			T0_PK009_CAP.setOpacity(1);
			if(autoYn[i] == 'Y'){
				T0_PK009_AUTO.setValue(1);
			}else{
				T0_PK009_AUTO.setValue(0);
			}
			break;
		case 'PK010':
			T0_PK010.setDisabled(false);
			break;
		case 'PK011':
			T0_PK011.setDisabled(false);
			break;
		case 'PK012':
			T0_PK012.setDisabled(false);
			break;
		case 'PK013':
			T0_PK013.setDisabled(false);
			break;
		case 'PK014':
			T0_PK014.setDisabled(false);
			break;
		case 'PK015':
