//FM0204G0그리드 cell 클릭 이벤트
scwin.FM0204G0_oncellclick = function(row,col,colId) {
	var rowCount = dlt_BMS_FCLT_COND_PARAM_INFO.getRowCount();

	//chk말고 다른 cell클릭시
	if (col != "0"){
		if(FM0204G0.getCellChecked(row, 'chk')==0){
			for(var i = 0; i < rowCount; i++){
				FM0204G0.setCellChecked(i, 'chk', false);			
			}
		   	FM0204G0.setCellChecked(row, 'chk', true);

			/*var chk = dlt_BMS_FCLT_COND_PARAM_INFO.getCellData(row, "chk");
			if(chk == 1){
				wfm_subBtn2.setDisabled(false);
				wfm_subBtn2.setOpacity(1);
			}else if(chk == 0){
				wfm_subBtn2.setDisabled(true);
				wfm_subBtn2.setOpacity(0.45);
			}*/
		}

		/*셀 클릭시 체크 지우기.
		else if(FM0204G0.getCellChecked(row, 'chk')==1){
		   	FM0204G0.setCellChecked(row, 'chk', false);
		}*/

		var chkCount = dlt_BMS_FCLT_COND_PARAM_INFO.getMatchedColumnData('chk', '1', 'chk');
		var chkLen = chkCount.length;

		//체크가 2개 이상일 때 자식 그리드와 폼 disable처리
		if(chkLen > 1 ){
			pgm_content2.hide();
			FM0204_ul.show("");

			//FM0204G1.setDisabled("grid", true);
			//FM0204F0.setDisabled(true);
		}else if(chkLen < 2){
			pgm_content2.show(""); //show사용할 때 "" 붙여야 원래 css적용 가능함!!!
			FM0204_ul.hide();

			//FM0204G1.setDisabled("grid", false);
			//FM0204F0.setDisabled(false);
		}

	}
	//chk 클릭시
	else if(col == "0"){

		checkBoxClick = true;

		var chkCount = dlt_BMS_FCLT_COND_PARAM_INFO.getMatchedColumnData('chk', '1', 'chk');
		var chkLen = chkCount.length;

		//체크가 2개 이상일 때 자식 그리드와 폼 disable처리
		pgm_content2.hide();
		FM0204_ul.show("");

		//FM0204G1.setDisabled("grid", true);
		//FM0204F0.setDisabled(true);
		/*
		else if(chkLen < 2){
			pgm_content2.show(""); //show사용할 때 "" 붙여야 원래 css적용 가능함!!!
			FM0204_ul.hide();

			//FM0204G1.setDisabled("grid", false);
			//FM0204F0.setDisabled(false);
		}
		*/
		var matchChk = dlt_BMS_FCLT_COND_PARAM_INFO.getMatchedData('chk', '1');

		//하위 버튼 disable 및 투명도 처리
		if(matchChk.length == 0 || matchChk.length == 1){
			wfm_subBtn2.setDisabled(false);
			wfm_subBtn2.setOpacity(1);
		}else if(matchChk.length > 1){
			wfm_subBtn2.setDisabled(true);
			wfm_subBtn2.setOpacity(0.45);
		}
	}
};

//배열 값 동일한지 테스트하는 함수
function allEqual(arr) {
  	return new Set(arr).size == 1;
}

//체크박스 header클릭시 이벤트
scwin.FM0204G0_onheaderclick = function(headerId) {
	if(headerId == 'chkHeader'){
		var rowCount = dlt_BMS_FCLT_COND_PARAM_INFO.getRowCount();
		var chk = dlt_BMS_FCLT_COND_PARAM_INFO.getColData("chk");

		var equalChk = allEqual(chk);

		if(equalChk == true){
			pgm_content2.hide();
			FM0204_ul.show("");
		}else{
			pgm_content2.show(""); //show사용할 때 "" 붙여야 원래 css적용 가능함!!!
			FM0204_ul.hide();
		}
	}
};

