	var paramKindJson = dlt_AIR_PARAM_KIND.getAllJSON();
	var rowCntParamKind = dlt_AIR_PARAM_KIND.getRowCount();
	//var rowCntArr = dlt_BMS_FCLT_COND_PARAM_INFO.getRowCount();
