	gridHeader +='<w2:column removeBorderStyle="false" width="90" inputType="text" style="height:20px" id="h2_colunm_2" value="정류소명" blockSelect="false" displayMode="label"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h2_colunm_3" value="시설물ID" blockSelect="false" displayMode="label" sortable="true"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="170" inputType="text" style="height:20px" id="h2_colunm_4" value="시설물종류" blockSelect="false" displayMode="label"></w2:column>';

	//동적 헤더 시작
	for(var i = 0; i < rowCntParamKind; i++) {
		var paramKind = dlt_AIR_PARAM_KIND.getCellData(i, "PARAM_KIND");

		if(paramKind != '' && paramKind != 'PK001'){
			var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
			var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");

			gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm'+i+3+'" value="'+dlCdNm+'" blockSelect="false" displayMode="label"></w2:column>';
			gridHeader +='<w2:column removeBorderStyle="false" width="110" inputType="text" style="height:20px" id="h_colunm'+i+4+'" value="'+dlCdNm+'자동'+'" blockSelect="false" displayMode="label"></w2:column>';
		}

	}

	//바디 시작
	//gridBody += '<w2:column blockSelect="false" disabled="false" falseValue="0" defaultValue="" trueValue="1" id="chk" readOnly="false" style="height:20px" inputType="checkbox" width="39"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="NODE_ID" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="NODE_NM" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="FCLT_ID" value="" blockSelect="false" displayMode="label"></w2:column>';
	//gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="drilldown" style="height:20px" id="FCLT_KIND_NM" value="" blockSelect="false" displayMode="label" showDepth="2" depthColumn="LEVEL" depthType="div" textAlign="left"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="text" style="height:20px" id="FCLT_KIND_NM" value="" blockSelect="false" displayMode="label"></w2:column>';

	//동적 바디 시작
	for(var i = 0; i < rowCntParamKind; i++) {
		var paramKind = dlt_AIR_PARAM_KIND.getCellData(i, "PARAM_KIND");
		var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
		var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");
