	var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" '
						+'autoFit="lastColumn" class="cm_grid left" dataList="data:dlt_BMS_FCLT_AIR_COND_PARAM_INFO" ev:oncellclick="scwin.FM0204G2_oncellclick" '
