	+'</w2:row>'
	+'</w2:header>'
	+'<w2:gBody style="" id="gBody2">'
	+'<w2:row id="row2" style="">'
	+ gridBody
	+'<w2:column removeBorderStyle="false" width="117" inputType="text" style="height:20px" id="" value="" blockSelect="false" displayMode="label"></w2:column>'
	+'</w2:row>'
	+'</w2:gBody>';
	gridStr += '<w2:footer id="footer1" style=""><w2:row id="row3" style=""><w2:column blockSelect="false" colSpan="30" displayMode="label" expression="&quot;데이터 개수 : &quot; + COUNT(\'COMP_ID\')" id="column42" inputType="expression" removeBorderStyle="false" style="height:20px;" textAlign="left" width="2386"></w2:column></w2:row></w2:footer>';
	gridStr += '</w2:gridView>';

	FM0204G2.setGridStyle(WebSquare.xml.parse(gridStr , true ));
	//dlt_BMS_FCLT_COND_PARAM_INFO.removeAll();
	autoOptions.Main.grid = FM0204G2;

};

//데이터유형이 ON/OFF인 경우 disformatter 추가 함수
scwin.onOffDisFormatter = function(data){
	if(data == ""){
		return "";
	}else if(data == '1'){
		return "ON";
	}else if(data == '0'){
		return "OFF";
	}
}

//데이터유형이 자동여부인 경우 disformatter 추가 함수
scwin.AutoDisFormatter = function(data){
	if(data == ""){
		return "";
	}else if(data == 'Y'){
		return "예";
	}else if(data == 'N'){
		return "아니요";
	}
}

scwin.sbx_ChoiceBox_onviewchange = function(info) {
	if (info.newValue == "ALL") {
		autoChoiceBox.hide();
		search_input.setValue("");
		search_input.show("");
	} else if (info.newValue == "NODE_ID") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_searchitem", "NODE_ID", "NODE_ID");
		autoChoiceBox.show("");
	} else if (info.newValue == "NODE_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_searchitem", "NODE_NM", "NODE_NM");
		autoChoiceBox.show("");
	}
};
