head.script:

var isVertexAdd;
var oldNodeType;
var curRoutId;

scwin.onpageload = function() {

	var codeOptions = [{code : "NODE_TYPE", compID : "SI0408G1:NODE_TYPE"},{code : "WAY_DIV", compID : "SI0408G0:WAY_DIV"}];
	com.setCommonDtl(codeOptions, function() {
		com.searchGrid(SI0408G0, sub_SI0408G0R0);
	});	

	com.executeSubmission(sub_SI0408SHI0);

	var autoOptions = {
		Main : { grid : SI0408G0, srchSbm : sub_SI0408G0R0, srchGrp : pgm_search, keyColumn : "ROUT_ID", focusColumn : "ROUT_ID", name : "노선 목록"},
		Sub1 : { grid : SI0408G1, srchSbm : sub_SI0408G1R0, srchGrp : pgm_sub_search, savSbm : sub_SI0408G1S0, name :  "노선 경로정보", search_map : dma_sub_search}
	 };

	var mainOptions = {
		ADD:{disp:"hide"},
		DEL:{disp:"hide"},
		CNL:scwin.btn_cancel_onclick,
		SEARCH : {}
	};

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions, mainOptions, codeOptions);

	var mainSrhOptions = {
	};	
	com.setMainSrhBtn(wfm_mainSearchBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions, mainSrhOptions, codeOptions);

	var subOptions = {
		DEL:scwin.btn_sub_del
		, SEARCH: scwin.btn_sub_move
	};

	com.setSubBtn2(wfm_subBtn, autoOptions, subOptions);

	var subOptions2 = {
		SAVE:[{cbFnc:scwin.addVertexClick,nm:"버텍스추가",class:"addVertex"},{cbFnc:scwin.btn_sub_del,nm:"선택삭제",class:"del"}
		]
	};

	com.setSubBtn2(wfm_subBtn2, autoOptions, subOptions2);

	SI0408G0.mergeCells("byrestriccol");


	routMap.initMap("map_SI0408", {width:"100%"
		, height:"100%"
		, onClick: scwin.onClickMap
		, nodeChangeCb: scwin.nodeChangeCb
		, draggable: true
		, isOverLayHidden : true
		, level : 0
	});


	scwin.disp_check_normal();
	//com.setExlUpdate(SI0408G1, false); //엑셀 업데이트 초기화
};


scwin.onpageunload = function() {

};

scwin.sub_SI0408G0R0_submitdone = function(e) {
	scwin.releaseGeoBtn();
	com.setFocusedCell(SI0408G0, "ROUT_ID");
};

scwin.sbx_ChoiceBox_onviewchange = function(info) {
	if (info.newValue == "ALL") {
		autoChoiceBox.hide();
		search_input.setValue("");
		search_input.show("");
	} 
	else if (info.newValue == "ROUT_ID") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_routItem", "ROUT_ID", "ROUT_ID");
		autoChoiceBox.show("");
	}
	else if (info.newValue == "ROUT_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_routItem", "ROUT_NM", "ROUT_NM");
		autoChoiceBox.show("");
	} else if (info.newValue == "REP_ROUT_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_routItem", "REP_ROUT_NM", "REP_ROUT_NM");
		autoChoiceBox.show("");
	}		
};

scwin.SI0408G0_onrowindexchange = function(row,oldRow) {

	routMap.initMapInfo("map_SI0408");
	curRoutId = dlt_BMS_ROUT_MST.getCellData(row,"ROUT_ID");
	dma_sub_search.set("ROUT_ID", dlt_BMS_ROUT_MST.getCellData(row,"ROUT_ID"));
	dma_sub_search.set("WAY_DIV", dlt_BMS_ROUT_MST.getCellData(row,"WAY_DIV"));
	var focusOption = {keyMap : dma_sub_search};
	com.changeDualGrid(SI0408G0, SI0408G1, sub_SI0408G1S0, sub_SI0408G1R0, focusOption, row, oldRow);
};

scwin.SI0408G1_onviewchange = function(info) {

	if(info.colIndex == 2){
		oldNodeType = info.oldValue;

		if (oldNodeType != "NT008" && info.newValue == "NT005") {
			com.alert("버텍스로 변경 할수 없습니다.");
			BMS_ROUT_NODE_DISP_CMPSTN.setCellData(SI0408G1.getFocusedRowIndex(),"NODE_TYPE", oldNodeType);
			return;
		}

		if (oldNodeType != "NT005" && info.newValue == "NT008") {
			com.alert("신호로 변경 할수 없습니다.");
			BMS_ROUT_NODE_DISP_CMPSTN.setCellData(SI0408G1.getFocusedRowIndex(),"NODE_TYPE", oldNodeType);
			return;
		}			

		if (info.newValue == "NT006") {
			com.alert("음성노드는 음성 편성에서만 선택할 수 있습니다.");
			BMS_ROUT_NODE_DISP_CMPSTN.setCellData(SI0408G1.getFocusedRowIndex(),"NODE_TYPE", oldNodeType);
			return;
		}

		if ((info.newValue == "NT001") || (info.newValue == "NT002")) { //교차로, 정류소 선택
			if(info.newValue == "NT002"){
				scwin.select_sttn(false);
			}
			else if(info.newValue == "NT001"){
				scwin.select_cross(false);
			}
		}
		else { //정류소가 아니면 정류소 정보 초기화, 교차로가 아니면 교차로 정보 초기화
			BMS_ROUT_NODE_DISP_CMPSTN.setCellData(SI0408G1.getFocusedRowIndex(), "NODE_NM", "노드_" + util.getCurrentDate().substring(4));
			BMS_ROUT_NODE_DISP_CMPSTN.setCellData(SI0408G1.getFocusedRowIndex(), "CRS_ID", "");
			BMS_ROUT_NODE_DISP_CMPSTN.setCellData(SI0408G1.getFocusedRowIndex(), "STTN_ID", "");
			BMS_ROUT_NODE_DISP_CMPSTN.setCellData(SI0408G1.getFocusedRowIndex(), "STTN_NO", "");
		}
	}
}

scwin.dma_disp_check_onviewchange = function(info) {

	scwin.releaseGeoBtn();
	routMap.setDispCheck("map_SI0408", disp_check.getValue());
	routMap.drawRoute("map_SI0408", SI0408G1, SI0408G1.getFocusedRowIndex());
}

scwin.SI0408G1_onrowindexchange = function(row,oldRow) {

	routMap.focusNode("map_SI0408",SI0408G1,row);
};

