		else if(nodeChildSn>=0){
			//SI0408G1.setRowColor(i, "#3396ff");
			SI0408G1.setRowColor(i, "#333aff");
		}
		/*else if(nodeChildSn>0){
			SI0408G1.setRowColor(i, "#333aff");
		}*/

		//SI0408G1.setCellBackgroundColor(i, "NODE_NM", "#3396ff");
	}


	//routMap.drawRoute("map_SI0408", SI0408G1, -1, true);

	/*for(var i =0; i < BMS_ROUT_NODE_DISP_CMPSTN.getRowCount(); i++){
		if(BMS_ROUT_NODE_DISP_CMPSTN.getCellData(i, "NODE_CHILD_SN")>=0){
			BMS_ROUT_NODE_DISP_CMPSTN.modifyRowStatus(i, "C");
		}
	}*/
};

scwin.sub_SI0408G1S0_submitdone = function(e) {
	var sCode = com.getResultCode(e);
	if (sCode == gcm.MESSAGE_CODE.STATUS_SAVE) {
		//com.setExlUpdate(SI0408G1, false); //엑셀 업데이트 초기화
		scwin.releaseGeoBtn();
		routMap.initMapInfo("map_SI0408");
		com.saveDone(SI0408G1,sub_SI0408G1R0);
	}
}

scwin.btn_cancel_onclick = function() {
	var idx = SI0408G1.getModifiedIndex().length;

	if (idx > 0) {
		var str =com.strModifiedCnt(SI0408G1) + "의 저장하지 않은 데이터가 있습니다. 취소 하시겠습니까?";
		com.confirm(str, function(rtn) {
			if (rtn) {
				com.clearGrid(SI0408G1);
				com.searchGrid(SI0408G1, sub_SI0408G1R0);
				//routMap.drawRoute("map_SI0408", SI0408G1, SI0408G1.getFocusedRowIndex(), false);
			}
		});
	}

};

//맵 클릭 이벤트
scwin.onClickMap = function(e) {
	curPoint = e;
	//scwin.disp_check_all();


	//var routNm = SI0408G0.getFocusedCellData("ROUT_NM");

	if(isVertexAdd){

		routMap.addDispVertexByClick("map_SI0408", SI0408G1,curRoutId,e);
	}
}

scwin.nodeChangeCb = function(idx) {
	if(isVertexAdd) return;

	SI0408G1.setFocusedCell(idx,"NODE_ID");

	var nodeType = BMS_ROUT_NODE_DISP_CMPSTN.getCellData(idx,"NODE_TYPE");

	if(nodeType == "NT002"){
		scwin.select_sttn(false);
	}
	else if(nodeType == "NT001"){
		scwin.select_cross(false);
	}
}


scwin.btn_del_onclick = function() {

};

scwin.btn_sub_del = function() {

	var focusIndex = SI0408G1.getFocusedRowIndex();
	var nodeChildSn = BMS_ROUT_NODE_DISP_CMPSTN.getCellData(focusIndex,"NODE_CHILD_SN");
	var nodeSn = BMS_ROUT_NODE_DISP_CMPSTN.getCellData(focusIndex,"NODE_SN");
	if(nodeChildSn==0){
		com.confirm("노선 경로에 있는 노드입니다. 삭제시 원 좌표로 변경됩니다", function(rtn){
			if (rtn) {
				BMS_ROUT_NODE_DISP_CMPSTN.setCellData(focusIndex, "GPS_X", BMS_ROUT_NODE_DISP_CMPSTN.getCellData(focusIndex,"ORG_GPS_X"));
				BMS_ROUT_NODE_DISP_CMPSTN.setCellData(focusIndex, "GPS_Y", BMS_ROUT_NODE_DISP_CMPSTN.getCellData(focusIndex,"ORG_GPS_Y"));
			}
			routMap.drawRoute("map_SI0408", SI0408G1, SI0408G1.getFocusedRowIndex(), false);
			return;
		});
	}
	else if(nodeChildSn>0){
		//com.delGrid(SI0408G1);
		com.delGrid(SI0408G1,null, function() {
				var j = 0;
				for(var i = 0; i < BMS_ROUT_NODE_DISP_CMPSTN.getRowCount(); i++){
					var status = BMS_ROUT_NODE_DISP_CMPSTN.getRowStatus(i);
					var mNodeSn = BMS_ROUT_NODE_DISP_CMPSTN.getCellData(i, "NODE_SN")
					if(nodeSn == mNodeSn&&status!="D"){
						if(j==0){
							BMS_ROUT_NODE_DISP_CMPSTN.modifyRowStatus(i, "C");
						}
						else {
							BMS_ROUT_NODE_DISP_CMPSTN.setCellData(i, "NODE_CHILD_SN",j);
						}
						j++;
					}
				}
				routMap.focusNode("map_SI0408",SI0408G1,SI0408G1.getFocusedRowIndex());
				//routMap.drawRoute("map_SI0408", SI0408G1, SI0408G1.getFocusedRowIndex(), false);
			}
		);
	}
};

scwin.btn_sub_move = function() {
	if(com.isEmpty(dma_sub_search.get("CONTENT"))==false){
		com.gridMove(SI0408G1, "NODE_ID,NODE_NM", dma_sub_search.get("CONTENT"), SI0408G1.getFocusedRowIndex());
	}
};



scwin.addVertexClick = function(){
	if(isVertexAdd) {
		isVertexAdd = false;
	} else {
		isVertexAdd = true;
	}
	scwin.changeGeoBtn();
}

scwin.refreshGridStatus = function(){
	var routeData = BMS_ROUT_NODE_DISP_CMPSTN.getAllJSON();
	for(var i = 0; i < routeData.length; i++) {
		if(routeData[i].NODE_TYPE == "NT001" || routeData[i].NODE_TYPE == "NT002"){
			SI0408G1.setCellReadOnly(i, "NODE_TYPE", true);
		}
	}
}

scwin.disp_check_all = function(){
	disp_check.getCheckboxList()[0].checked = true;
	disp_check.getCheckboxList()[1].checked = true;
	disp_check.getCheckboxList()[2].checked = true;
	disp_check.getCheckboxList()[3].checked = true;
	routMap.setDispCheck("map_SI0408", disp_check.getValue());
}

scwin.disp_check_normal = function(){
	disp_check.getCheckboxList()[0].checked = true;
	disp_check.getCheckboxList()[1].checked = true;
	disp_check.getCheckboxList()[2].checked = true;
	disp_check.getCheckboxList()[3].checked = true;
	routMap.setDispCheck("map_SI0408", disp_check.getValue());
}



scwin.releaseGeoBtn = function(){
	isVertexAdd = false;
	scwin.changeGeoBtn();
}

scwin.changeGeoBtn = function(){

	var temp = wfm_subBtn2.getWindow().btn_sub_generator;

	if(isVertexAdd) {
		temp.getChild(0,"btn_sub").setValue("버텍스종료");
		temp.getChild(0,"btn_sub").addClass("end");
	}
	else {
		temp.getChild(0,"btn_sub").setValue("버텍스추가");
		temp.getChild(0,"btn_sub").removeClass("end");
	}

	if(!isVertexAdd) {
		routMap.setMapCursor("map_SI0408", "pointer");
	}
	else {
		scwin.disp_check_all();
		routMap.setMapCursor("map_SI0408", "crosshair");
	}
}
