	scwin.sub_selectSttnListByRepRout_submitdone = function(e) {
		//scwin.drawRoutMap(dlt_BMS_ROUT_NODE_CMPSTN2);
	};	

	//세로 한 부분
	var vertical = 0;
	//가로 전체 개수
	var sumHorizontal = 0;
	//가로 위쪽
	var topHorizontal = 0;
	//가로 아래쪽
	var botHorizontal = 0;	

	//노선, 정류소, 교차로 그리기
	scwin.drawRoutMap = function(sttnCrsList, sttnList) {
		//전체 데이터 이용해서 조건문 걸어서 Crs와 Sttn 값 다르게 설정해주면 될 거 같음. vertical 변수부터 전부 다시 설정해야함
		$(".route-map").empty();
		var sttnListLength = sttnList.getAllJSON().length;
		var sttnCrsListLength = sttnCrsList.getAllJSON().length;

		//교통 상황에 따라 색깔변화, 고쳐야함
		var routLineColor = "smooth";

		var routDivResult = "";
		var sttnDivResult = "";
		var crsDivResult = "";

		var sttnCrsCount = 0;

		/*
		//세로 한 부분
		var vertical = 0;
		//가로 전체 개수
		var sumHorizontal = 0;
		//가로 위쪽
		var topHorizontal = 0;
		//가로 아래쪽
		var botHorizontal = 0;
		*/
		if(sttnListLength < 30){
			vertical = 2;
		}

		else if(sttnListLength > 30 && sttnListLength < 40) {
			vertical = 3;
		}

		else {
			vertical = 4;	
		}

		//sumHorizontal = sttnListLength - (vertical*2); 
		sumHorizontal = sttnCrsListLength - (vertical*2); 

		if(sumHorizontal %2 != 0) {
			topHorizontal = parseInt(sumHorizontal / 2);
			botHorizontal = parseInt(sumHorizontal / 2) - 1;	
		}

		else if(sumHorizontal % 2 == 0) {
			topHorizontal = sumHorizontal / 2;
			botHorizontal = sumHorizontal / 2;				
		}

		//가로 위쪽
		for (var i=-1; i<topHorizontal; i++) {
			routDivResult += '<div class="route-line h-line '+ routLineColor +'" style="width:calc(100% / '+ topHorizontal+ '); '; 
			routDivResult += 'top:calc(100% / '+vertical+' * '+ 0 +');left:calc(100% / '+topHorizontal+' * '+ i +')"></div>';
			if(dlt_BMS_STTNCRS_LINK_CMPSTN.getCellData(sttnCrsCount, "NODE_TYPE") == "NT001") {
				crsDivResult += '<div class="cross" style="top:calc(100% / ' + vertical +'* ' + 0 + ');left:calc(100% / '+ topHorizontal +' * ' + i + ')">';	
				crsDivResult += '<span class="name">' + sttnCrsList.getCellData(sttnCrsCount, "NODE_NM") +'</span>';
				crsDivResult += '<span class="nodeId" style="display:none" data-value="' + sttnCrsList.getCellData(sttnCrsCount, "NODE_ID") +'"></span></div>';
			}
			else if(dlt_BMS_STTNCRS_LINK_CMPSTN.getCellData(sttnCrsCount, "NODE_TYPE") == "NT002") {
				sttnDivResult += '<div class="station right" style="top:calc(100% / ' + vertical + ' * '+ 0 +');left:calc(100% / '+ topHorizontal +' * '+ i + ')">';
				sttnDivResult += '<span class="name">' + sttnCrsList.getCellData(sttnCrsCount, "NODE_NM") +'</span>';
				sttnDivResult += '<span class="nodeId" style="display:none" data-value="' + sttnCrsList.getCellData(sttnCrsCount, "NODE_ID") +'"></span></div>';				
			}
			sttnCrsCount++;
		}
		//세로 오른쪽
		for (var i=0; i<vertical; i++) {
			routDivResult += '<div class="route-line v-line '+ routLineColor +'" style="height:calc(100% / '+ vertical+ '); '; 
			routDivResult += 'top:calc(100% / '+vertical+' * '+ i +');left:calc(100% / '+topHorizontal+' * '+ topHorizontal +')"></div>';
			if(dlt_BMS_STTNCRS_LINK_CMPSTN.getCellData(sttnCrsCount, "NODE_TYPE") == "NT001") {
				crsDivResult += '<div class="cross" style="top:calc(100% / ' + vertical +'* ' + i + ');left:calc(100% / '+ topHorizontal +' * ' + topHorizontal + ')">';
				crsDivResult += '<span class="name">' + sttnCrsList.getCellData(sttnCrsCount, "NODE_NM") +'</span>';
				crsDivResult += '<span class="nodeId" style="display:none" data-value="' + sttnCrsList.getCellData(sttnCrsCount, "NODE_ID") +'"></span></div>';	
			}			
			else if(dlt_BMS_STTNCRS_LINK_CMPSTN.getCellData(sttnCrsCount, "NODE_TYPE") == "NT002") {
				sttnDivResult += '<div class="station down" style="top:calc(100% / ' + vertical +' * '+ i +');left:calc(100% / '+ topHorizontal +' * '+ topHorizontal + ')">';
				sttnDivResult += '<span class="name">' + sttnCrsList.getCellData(sttnCrsCount, "NODE_NM") +'</span>';
				sttnDivResult += '<span class="nodeId" style="display:none" data-value="' + sttnCrsList.getCellData(sttnCrsCount, "NODE_ID") +'"></span></div>';	
			}	
			sttnCrsCount++;	
		}
		//가로 아래쪽
		for (var i=topHorizontal-1; i>=0; i--) {
			routDivResult += '<div class="route-line h-line '+ routLineColor +'" style="width:calc(100% / '+ topHorizontal+ '); '; 
			routDivResult += 'top:calc(100% / '+vertical+' * '+ vertical +');left:calc(100% / '+topHorizontal+' * '+ i +')"></div>';
			if(dlt_BMS_STTNCRS_LINK_CMPSTN.getCellData(sttnCrsCount, "NODE_TYPE") == "NT001") {
				crsDivResult += '<div class="cross" style="top:calc(100% / ' + vertical +'* ' + vertical + ');left:calc(100% / '+ topHorizontal +' * ' + (i+1) + ')">';
				crsDivResult += '<span class="name">' + sttnCrsList.getCellData(sttnCrsCount, "NODE_NM") +'</span>';
				crsDivResult += '<span class="nodeId" style="display:none" data-value="' + sttnCrsList.getCellData(sttnCrsCount, "NODE_ID") +'"></span></div>';	
			}			
			else if(dlt_BMS_STTNCRS_LINK_CMPSTN.getCellData(sttnCrsCount, "NODE_TYPE") == "NT002") {
				sttnDivResult += '<div class="station left" style="top:calc(100% / ' + vertical + ' * '+ vertical +');left:calc(100% / '+ topHorizontal +' * '+ (i+1) + ')">';
				sttnDivResult += '<span class="name">' + sttnCrsList.getCellData(sttnCrsCount, "NODE_NM") +'</span>';
				sttnDivResult += '<span class="nodeId" style="display:none" data-value="' + sttnCrsList.getCellData(sttnCrsCount, "NODE_ID") +'"></span></div>';	
			}		
			sttnCrsCount++;
		}
		//세로 왼쪽
		for (var i=vertical-1; i>=0; i--) {
			routDivResult += '<div class="route-line v-line '+ routLineColor +'" style="height:calc(100% / '+ vertical+ '); '; 
			routDivResult += 'top:calc(100% / '+vertical+' * '+ i +');left:calc(100% / '+topHorizontal+' * '+ 0 +')"></div>';
			if(dlt_BMS_STTNCRS_LINK_CMPSTN.getCellData(sttnCrsCount, "NODE_TYPE") == "NT001") {
				crsDivResult += '<div class="cross" style="top:calc(100% / ' + vertical +'* ' + (i+1) + ');left:calc(100% / '+ topHorizontal +' * ' + 0 + ')">';
				crsDivResult += '<span class="name">' + sttnCrsList.getCellData(sttnCrsCount, "NODE_NM") +'</span>';
				crsDivResult += '<span class="nodeId" style="display:none" data-value="' + sttnCrsList.getCellData(sttnCrsCount, "NODE_ID") +'"></span></div>';	
			}	
			else if(dlt_BMS_STTNCRS_LINK_CMPSTN.getCellData(sttnCrsCount, "NODE_TYPE") == "NT002") {		
				sttnDivResult += '<div class="station up" style="top:calc(100% / ' + vertical +' * '+ (i+1) +');left:calc(100% / '+ topHorizontal +' * '+ 0 + ')">';
				sttnDivResult += '<span class="name">' + sttnCrsList.getCellData(sttnCrsCount, "NODE_NM") +'</span>';
				sttnDivResult += '<span class="nodeId" style="display:none" data-value="' + sttnCrsList.getCellData(sttnCrsCount, "NODE_ID") +'"></span></div>';	
			}			
			sttnCrsCount++;
		}				

		$(".route-map").append(routDivResult);
		$(".route-map").append(sttnDivResult);
		$(".route-map").append(crsDivResult);

		scwin.drawSig(sttnCrsList);
	};

	scwin.sub_selectBus_submitdone = function(e) {
		for(var i=0; i<dlt_BRT_CUR_OPER_INFO.getAllJSON().length; i++) {
			scwin.drawBus(dlt_BRT_CUR_OPER_INFO.getDataRow(i), dlt_BMS_STTNCRS_LINK_CMPSTN);
		}
		clearInterval(refreshTime);
		refreshTime = setInterval(function() {
			com.executeSubmission(sub_selectBus);
		},parseInt(refPeriod.getValue()));		
		var test = null;		
	};

	//버스 그리기
	scwin.drawBus = function(busData, sttnCrsList) {
		$(".bus").remove();
		var nextNodeId = busData.NEXT_NODE_ID;
		var vhcKind = busData.VHC_KIND;
		var busColor = "";
		var busDivResult = "";
		var busNo = busData.VHC_NO;

		switch(vhcKind) {
			case "VK001":
			busColor = "red";
			break;

			case "VK002":
			busColor = "blue";
			break;

			case "VK003":
			busColor = "black";
			break;

			default:
			busColor = "black";	
			break;
		}


		var matchedIndexArr = sttnCrsList.getMatchedIndex("NEXT_NODE_ID", nextNodeId);
		var sttnCrsRowData = sttnCrsList.getDataRow(matchedIndexArr[0]);

		//버스와 다음 교차로/정류소까지의 거리
		var distance = getDistanceBetween(busData.GPS_X, busData.GPS_Y, sttnCrsRowData.NEXT_GPS_X, sttnCrsRowData.NEXT_GPS_Y);
		//km 변환
		distance = distance / 1000;
		distance = Math.round(distance * 100) / 100;

		if(matchedIndexArr[0] != 0) {
//			prevNodeGPS_X = sttnCrsList.getCellData(matchedIndexArr[0]-1, "GPS_X");
//			prevNodeGPS_Y = sttnCrsList.getCellData(matchedIndexArr[0]-1, "GPS_Y");
//			var prevNodeDistance = 	getDistanceBetween(prevNodeGPS_X, prevNodeGPS_Y, sttnCrsRowData.GPS_X, sttnCrsRowData.GPS_Y);
//			prevNodeDistance = prevNodeDistance / 1000;
//			prevNodeDistance = Math.round(prevNodeDistance * 100) / 100;
			var matchedLinkLen = sttnCrsList.getCellData(matchedIndexArr[0], "LEN");
			matchedLinkLen = matchedLinkLen / 1000;
			matchedLinkLen = Math.round(matchedLinkLen * 100) / 100;
			//링크에서 버스의 거리 비율
			//var distanceRatio = matchedLinkLen /  distance;
			var distanceRatio = (1-distance) / matchedLinkLen;
			//distanceRatio = 0.5 + (distanceRatio * 0.5);
			distanceRatio = Math.round(distanceRatio * 1000) / 1000;
			var parentDiv = $(".route-map").find('.nodeId[data-value='+nextNodeId+']').parent();
			var parentStyle = parentDiv.attr("style").split(";");
			//var parentTop = parentDiv.css("top").replace("px","");
			//var parentLeft = parentDiv.css("left").replace("px","");
			//x * y
			var parentTop = parentStyle[0].match(/.+\/(.+)\)/)[1].trim();
			var parentLeft = parentStyle[1].match(/.+\/(.+)\)/)[1].trim();

			var busDivTop = "";
			var busDivLeft = "";

			//위
			if(eval(parentTop) == 0) {
				//if(parentLeft != topHorizontal) {
					distanceRatio = 0.5 + (distanceRatio * 0.5);
					busDivTop = parentTop;
					busDivLeft = parentLeft +'*'+ distanceRatio;	
				//}
			}

			//아래 오른쪽 왼쪽 테스트 필요함
			//아래
			else if(eval(parentTop) == vertical * vertical && eval(parentLeft) != topHorizontal * topHorizontal) {
				distanceRatio = 1.5 + (distanceRatio * 1.5);
				busDivTop = vertical;
				//busDivLeft = parentLeft / distanceRatio; 
				busDivLeft = parentLeft +'*'+ distanceRatio; 
			}

			//오른쪽
			else if(eval(parentLeft) == topHorizontal * topHorizontal) {
				distanceRatio = 1.5 + (distanceRatio * 1.5);
				busDivTop = parentTop + '*' + distanceRatio;
				busDivLeft = parentLeft; 
			}

			//왼쪽
			else if(eval(parentLeft) == 0) {
				distanceRatio = 0.5 + (distanceRatio * 0.5);
				busDivTop = vertical;
				busDivLeft = parentLeft; 
			}

			//else if(parentTop != "0" && parentTop != vertical && parentLeft = ) {

			//}
			//busDivResult += '<div class="bus ' + busColor +'" style="top:calc(100% / '+ parentTop+' * '+busDivTop+'); left:calc(100% / '+ parentLeft +' * '+busDivLeft+')">';
			//busDivResult += '<div class="bus ' + busColor +'" style="top:calc(100% /'+ vertical+' * '+busDivTop+');left:calc(100% /'+ parentLeft +' * '+busDivLeft+')">';
			busDivResult += '<div class="bus ' + busColor +'" style="top:calc(100% /'+ busDivTop+');left:calc(100% /'+ busDivLeft+')">';
			busDivResult += '<span class="number">'+busNo+'</span></div>'
			$(".route-map").append(busDivResult);
		}

	}

	scwin.sub_selectCrsListByRepRout_submitdone = function(e) {

