		},parseInt(refPeriod.getValue()));
	};

	scwin.drawSig = function(sttnCrsList) {
		var sttnCrsListLength = sttnCrsList.getAllJSON().length;
		var crsDivResult = "";

		for(var i=0; i<sttnCrsListLength; i++) {
			var phaseCnt = sttnCrsList.getCellData(i, "PHASE_CNT");
			if (phaseCnt != "") {
				var crsId = sttnCrsList.getCellData(i, "CRS_ID");
				var parentDiv = $(".route-map").find('.nodeId[data-value='+crsId+']').parent();

				var parentStyle = parentDiv.attr("style").split(";");

				//x * y
				var parentTop = parentStyle[0].match(/.+\/(.+)\)/)[1].trim();
				var parentLeft = parentStyle[1].match(/.+\/(.+)\)/)[1].trim();				

				crsDivResult += '<div class="light green" style="top:calc(100% / ' + parentTop+'); left:calc(100% / '+ parentLeft+')">';
				crsDivResult += '<span class="sigCrsId" style="display:none" data-value="' + crsId +'"></span></div>';					
			}
		}
		$(".route-map").append(crsDivResult);
	};

