scwin.sub_test_submitdone = function(e) {
	com.setFocusedCell(VD0204G0, "VHC_ID");
};

scwin.selectReset = function() {
	com.alert("선택 리셋입니다. (후 작업 필요)");
}

scwin.selectControl = function() {
	com.alert("선택 전송입니다. (후 작업 필요)");
}

scwin.paramPopupOnclick = function() {
	var focusRowIdx = scwin.idxRowAuthority;
	var dvcKind = dlt_BMS_DVC_INFO.getCellData(VD0204G1.getFocusedRowIndex(), "DVC_KIND")
	dma_subsearch.set("DVC_KIND", dvcKind);

	var paramKind = dlt_BMS_DVC_PARAM_CFG_INFO.getColData("PARAM_KIND");
	dma_subsearch.set("PARAM_ECPT_IDS", paramKind);

	if (focusRowIdx == -1) {
		com.alert("장치 선택후 파라미터를 등록하실 수 있습니다.");
	} else {
		var DVC_KIND = dma_subsearch.get("DVC_KIND");
		if (DVC_KIND != null) {
			var data = { data : dma_subsearch.getJSON(), callbackFn : "scwin.insertPopupData" };
			var options = { id : "VD0204P01",
							popupName : "파라미터 추가", 
							modal : true};
			com.openPopup("/ui/BMS/VD0204/VD0204P01.xml", options, data);
		}			
	}
}

scwin.insertPopupData = function(obj) {
	var dLength = obj.length;
	for (i = 0; i < dLength; i++) {
		var data = dlt_BMS_DVC_INFO.getMatchedIndex("DVC_KIND", obj[i].DVC_KIND, true);
		obj[i].DVC_ID = dma_subsearch.get("DVC_ID");
		obj[i].VHC_ID = dma_subsearch.get("VHC_ID");
		obj[i].chk = "0";
		var idx = dlt_BMS_DVC_PARAM_CFG_INFO.insertRow();
		dlt_BMS_DVC_PARAM_CFG_INFO.setRowJSON(idx, obj[i], true);
	}
	com.setFocusedCell(VD0204G2, "DVC_KIND");


};

//조회 콜백 메소드
scwin.sub_VD0204G0R0_submitdone = function(e) {
	com.setFocusedCell(VD0204G0, "VHC_ID");
};

scwin.sbx_ChoiceBox_onviewchange = function(info) {
	if (info.newValue == "ALL") {
		autoChoiceBox.hide();
		search_input.setValue("");
		search_input.show("");
	} else if (info.newValue == "VHC_ID") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_searchitem", "VHC_ID", "VHC_ID");
		autoChoiceBox.show("");
	} else if (info.newValue == "VHC_NO") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_searchitem", "VHC_NO", "VHC_NO");
		autoChoiceBox.show("");
	}
};

scwin.VD0204G0_onrowindexchange = function(row,oldRow) {
	dma_subsearch.set("VHC_ID", dlt_BMS_VHC_MST.getCellData(row, "VHC_ID"))
	var focusOption = {keyMap : dma_subsearch};
	com.changeDualGrid(VD0204G0, VD0204G1, null, sub_VD0204G1R0, focusOption, row, oldRow);
};

scwin.sub_VD0204G1R0_submitdone = function(e) {
	com.setFocusedCell(VD0204G1, "DVC_ID");		
};

scwin.VD0204G1_onrowindexchange = function(row,oldRow) {
	dma_subsearch.set("DVC_ID", dlt_BMS_DVC_INFO.getCellData(row, "DVC_ID"))
	com.executeSubmission(sub_VD0204G2R0);

	//var focusOption = {keyMap : dma_subsearch};
	//com.changeDualGrid(VD0204G1, VD0204G2, null, sub_VD0204G2R0, focusOption, row, oldRow);
};

scwin.sub_VD0204G2R0_submitdone = function(e) {
	com.setFocusedCell(VD0204G2, "DVC_KIND");		
};

scwin.sub_VD0204G2S0_submitdone = function(e) {
	var sCode = com.getResultCode(e);
	if (sCode == gcm.MESSAGE_CODE.STATUS_SAVE) {
		com.saveDone(VD0204G2,sub_VD0204G2R0);
	}
};

scwin.onOffDisFormatter = function(data){
	if(data == ""){
		return "";
	}else if(data == '1'){
		return "ON";
	}else if(data == '0'){
		return "OFF";
	}
}

scwin.useYnDisFormatter = function(data){
	if(data == ""){
		return "";
	}else if(data == 'Y'){
		return "사용";
	}else if(data == 'N'){
		return "사용안함";
	}
}

scwin.autoYnDisFormatter = function(data){
	if(data == ""){
		return "";
	}else if(data == 'Y'){
		return "사용";
	}else if(data == 'N'){
		return "사용안함";
	}
}

