var autoOptions = [];

var checkBoxClick = false;

scwin.onpageload = function() {
	autoOptions = {
		Main : { grid : VD0204G0, srchSbm : sub_VD0204G0R0, srchGrp : pgm_header, keyColumn : "VHC_ID", focusColumn : "VHC_NO", name :  "차량 목록"},
		Sub1 : { grid : VD0204G1, srchSbm : sub_VD0204G1R0, name :  "장치 목록"}
	 };

	var mainOptions = {
			ADD:{cbFnc:scwin.selectReset,nm:"일괄리셋", class:"reset"}
			,DEL:{}
			,CNL:{}
			,SAVE:{}
	}

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions, mainOptions);

	var subOptions2 = {
		ADD:[{cbFnc:scwin.selectControl,nm:"전송", class:"send"}]
	};
	com.setSubBtn2(wfm_subBtn2, autoOptions, subOptions2);

	var codeOptions = [ {code : "AREA", compID : "VD0204G0:AREA" },
						{code : "VHC_KIND", compID : "VD0204G0:VHC_KIND"},
						{code : "ROUT_TYPE", compID : "VD0204G0:ROUT_TYPE"},
						{code : "VHC_TYPE", compID : "VD0204G0:VHC_TYPE"},
						{code : "VHC_FUEL", compID : "VD0204G0:VHC_FUEL"},
						{code : "DVC_KIND", compID : "VD0204G1:DVC_KIND"},
						{code : "INST_LOC", compID : "VD0204G1:INST_LOC"},
						{code : "PARAM_KIND"}
	 ];

	com.setCommonDtl(codeOptions, function() {
		com.searchGrid(VD0204G0, sub_VD0204G0R0);
	});

	com.executeSubmission(sub_VD0204SHI0);	
	com.executeSubmission(sub_VD0204SHI1);	
	com.executeSubmission(sub_vhcItem);	

	$("#VD0204_TAB").attr("style", "width:200px; height:26px; margin-left: 10px; float:left");
	$(".pgm_list2").css("display", "none");
	VD0204_ul.hide();

	//탭 목록 클릭 이벤트
	$(".ctl_dvc").find("li").click( function() {
		var li_id = this.id;
		$(".ctl_dvc").find("li").removeClass("on");
		$(".ctl_dvc").find("#"+li_id).addClass("on");

		if(li_id == "li_OBE") {
			$(".pgm_list").css("display", "");
			$(".pgm_list2").css("display", "none");
		}
		else if(li_id == "li_tagless") {
			$("#VD0204_TAB2").attr("style", "width:200px; height:26px; margin-left: 10px; float:left");
			$(".pgm_list").css("display", "none");
			$(".pgm_list2").css("display", "");
			com.executeSubmission(sub_VD0204G2R0);	
		}
	});
};

