//동적 그리드 함수
scwin.setGridViewInfo = function() {
	var gridHeader ="", gridBody="";
	var rowData = dlt_BMS_DVC_COND_PARAM_INFO.getAllJSON();
	var paramKindJson = dlt_PARAM_KIND.getAllJSON();
	var rowCntParamKind = dlt_PARAM_KIND.getRowCount();
	//var rowCntArr = dlt_BMS_DVC_COND_PARAM_INFO.getRowCount();
	//var rowCntParamKind = dlt_commonDtlPARAM_KIND.getRowCount();

	//헤더 시작
	gridHeader +='<w2:column width="40" inputType="checkbox" style="height:20px" id="chkHeader" value="선택" blockSelect="false" sortable="false"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm_1" value="차량ID" blockSelect="false" displayMode="label" sortable="true"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="90" inputType="text" style="height:20px" id="h_colunm_2" value="차량번호" blockSelect="false" displayMode="label"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm_3" value="장치ID" blockSelect="false" displayMode="label" sortable="true"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="170" inputType="text" style="height:20px" id="h_colunm_4" value="장치종류" blockSelect="false" displayMode="label"></w2:column>';


	//동적 헤더 시작
	for(var i = 0; i < rowCntParamKind; i++) {
		var paramKind = dlt_PARAM_KIND.getCellData(i, "PARAM_KIND");

		if(paramKind != '' && paramKind != 'PK001'){
			var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
			var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");

			gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm'+i+3+'" value="'+dlCdNm+'" blockSelect="false" displayMode="label"></w2:column>';
		}

	}

	//바디 시작
	gridBody += '<w2:column blockSelect="false" disabled="false" falseValue="0" defaultValue="" trueValue="1" id="chk" readOnly="false" style="height:20px" inputType="checkbox" width="39"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="VHC_ID" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="VHC_NO" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="DVC_ID" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="drilldown" style="height:20px" id="DVC_KIND_NM" value="" blockSelect="false" displayMode="label" showDepth="2" depthColumn="LEVEL" depthType="div" textAlign="left"></w2:column>';


	//동적 바디 시작
	for(var i = 0; i < rowCntParamKind; i++) {
		var paramKind = dlt_PARAM_KIND.getCellData(i, "PARAM_KIND");
		var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
		var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");
		var displayForMatter = '';

		if(dlCd == 'PK002' || dlCd == 'PK003' || dlCd == 'PK006' || dlCd == 'PK007'){
			displayForMatter = 'scwin.onOffDisFormatter'; //데이터유형이 ON/OFF인 경우 displayformaatter 추가
		}

		if(paramKind != '' && paramKind != 'PK001'){
			gridBody +='<w2:column removeBorderStyle="false" width="113" inputType="text" style="height:20px" id="'+paramKind+'" value="" blockSelect="false" displayMode="label" displayFormatter="'+displayForMatter+'"></w2:column>';
		}

	}

	//gridView 정보 정의 
	//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
	var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" '
						+'autoFit="lastColumn" class="cm_grid left" dataList="data:dlt_BMS_DVC_COND_PARAM_INFO" ev:oncellclick="scwin.VD0204G0_oncellclick" '
						+'ev:onrowindexchange="scwin.VD0204G0_onrowindexchange" fixedColumnWithHidden="true" id="VD0204G0" ignoreCellClick="false" rowNumHeaderValue="순번" rowNumVisible="true" '
						+'rowStatusVisible="false" rowStatusWidth="40" scrollByColumn="false" style="" useShiftKey="true" autoFitMinWidth="0" readOnly="true" '
						+'sortable="false" focusMode="row" showSortableImage="true" sortEvent="onclick" ev:onheaderclick="scwin.VD0204G0_onheaderclick">';
	gridStr += '<w2:header style="" id="header2">'
	+ '<w2:row style="" id="row3">'
	+ gridHeader
	+'<w2:column removeBorderStyle="false" width="170" inputType="text" style="height:20px" id="h_colunm_5" value="" blockSelect="false" displayMode="label"></w2:column>'
	+'</w2:row>'
	+'</w2:header>'
	+'<w2:gBody style="" id="gBody2">'
	+'<w2:row id="row2" style="">'
	+ gridBody
	+'<w2:column removeBorderStyle="false" width="117" inputType="text" style="height:20px" id="" value="" blockSelect="false" displayMode="label"></w2:column>'
	+'</w2:row>'
	+'</w2:gBody>';
	gridStr += '<w2:footer id="footer1" style=""><w2:row id="row3" style=""><w2:column blockSelect="false" colSpan="30" displayMode="label" expression="&quot;데이터 개수 : &quot; + COUNT(\'COMP_ID\')" id="column42" inputType="expression" removeBorderStyle="false" style="height:20px;" textAlign="left" width="2386"></w2:column></w2:row></w2:footer>';
	gridStr += '</w2:gridView>';

	VD0204G0.setGridStyle(WebSquare.xml.parse(gridStr , true ));
	//dlt_BMS_DVC_COND_PARAM_INFO.removeAll();
	autoOptions.Main.grid = VD0204G0;

};

//VD0204G0그리드 row 변경 이벤트
scwin.VD0204G0_onrowindexchange = function(row,oldRow) {

	if (checkBoxClick == false) {
		dma_subsearch.set("MNG_ID", dlt_BMS_DVC_COND_PARAM_INFO.getCellData(row, "MNG_ID"));
		var focusOption = {keyMap : dma_subsearch};
		com.changeDualGrid(VD0204G0, VD0204G1, null, sub_VD0204G1R0, focusOption, row, oldRow);
	}else {
		checkBoxClick = false;	
	}

	dma_subsearch.set("DVC_ID", dlt_BMS_DVC_COND_PARAM_INFO.getCellData(row, "DVC_ID"));
	com.executeSubmission(sub_VD0204G0R2);

};

scwin.sub_VD0204G0R2_submitdone = function(e) {

	var paramKind = dlt_CTL_PARAM_KIND.getColData("PARAM_KIND");
	//var rowCount = dlt_CTL_PARAM_KIND.getRowCount();
	//var t_paramkind = ['T_PK004', 'T_PK005', 'T_PK011', 'T_PK016', 'T_PK017'];

	T_PK004.setDisabled(true);
	T_PK005.setDisabled(true);
	T_PK011.setDisabled(true);
	T_PK016.setDisabled(true);
	T_PK017.setDisabled(true);

	for(var i = 0; i < paramKind.length; i++){
		switch (paramKind[i]) {
		case 'PK004':
			T_PK004.setDisabled(false);
			break;
		case 'PK005':
			T_PK005.setDisabled(false);
			break;
		case 'PK011':
			T_PK011.setDisabled(false);
			break;
		case 'PK016':
			T_PK016.setDisabled(false);
			break;
		case 'PK017':
			T_PK017.setDisabled(false);
			break;
		default:
			break;
		}
	}

};

//VD0204G0그리드 cell 클릭 이벤트
scwin.VD0204G0_oncellclick = function(row,col,colId) {
	var rowCount = dlt_BMS_DVC_COND_PARAM_INFO.getRowCount();

	//chk말고 다른 cell클릭시
	if (col != "0"){
		if(VD0204G0.getCellChecked(row, 'chk')==0){
			for(var i = 0; i < rowCount; i++){
				VD0204G0.setCellChecked(i, 'chk', false);			
			}
		   	VD0204G0.setCellChecked(row, 'chk', true);

			var chk = dlt_BMS_DVC_COND_PARAM_INFO.getCellData(row, "chk");
			if(chk == 1){
				wfm_subBtn2.setDisabled(false);
				wfm_subBtn2.setOpacity(1);
			}else if(chk == 0){
				wfm_subBtn2.setDisabled(true);
				wfm_subBtn2.setOpacity(0.45);
			}
		}

		/*셀 클릭시 체크 지우기.
		else if(VD0204G0.getCellChecked(row, 'chk')==1){
		   	VD0204G0.setCellChecked(row, 'chk', false);
		}*/

		var chkCount = dlt_BMS_DVC_COND_PARAM_INFO.getMatchedColumnData('chk', '1', 'chk');
		var chkLen = chkCount.length;

		//체크가 2개 이상일 때 자식 그리드와 폼 disable처리
		if(chkLen > 1 ){
			pgm_content2.hide();
			VD0204_ul.show("");

			//VD0204G1.setDisabled("grid", true);
			//VD0204F0.setDisabled(true);
		}else if(chkLen < 2){
			pgm_content2.show(""); //show사용할 때 "" 붙여야 원래 css적용 가능함!!!
			VD0204_ul.hide();

			//VD0204G1.setDisabled("grid", false);
			//VD0204F0.setDisabled(false);
		}

	}
	//chk 클릭시
	else if(col == "0"){

		checkBoxClick = true;

		var chkCount = dlt_BMS_DVC_COND_PARAM_INFO.getMatchedColumnData('chk', '1', 'chk');
		var chkLen = chkCount.length;

		//체크가 2개 이상일 때 자식 그리드와 폼 disable처리
		pgm_content2.hide();
		VD0204_ul.show("");

		//VD0204G1.setDisabled("grid", true);
		//VD0204F0.setDisabled(true);
		/*
		else if(chkLen < 2){
			pgm_content2.show(""); //show사용할 때 "" 붙여야 원래 css적용 가능함!!!
			VD0204_ul.hide();

			//VD0204G1.setDisabled("grid", false);
			//VD0204F0.setDisabled(false);
		}
		*/
		var matchChk = dlt_BMS_DVC_COND_PARAM_INFO.getMatchedData('chk', '1');

		//하위 버튼 disable 및 투명도 처리
		if(matchChk.length == 0 || matchChk.length == 1){
			wfm_subBtn2.setDisabled(false);
			wfm_subBtn2.setOpacity(1);
		}else if(matchChk.length > 1){
			wfm_subBtn2.setDisabled(true);
			wfm_subBtn2.setOpacity(0.45);
		}
	}
};

//배열 값 동일한지 테스트하는 함수
function allEqual(arr) {
  	return new Set(arr).size == 1;
}

//체크박스 header클릭시 이벤트
scwin.VD0204G0_onheaderclick = function(headerId) {
	if(headerId == 'chkHeader'){
		var rowCount = dlt_BMS_DVC_COND_PARAM_INFO.getRowCount();
		var chk = dlt_BMS_DVC_COND_PARAM_INFO.getColData("chk");

		var equalChk = allEqual(chk);

		if(equalChk == true){
			pgm_content2.hide();
			VD0204_ul.show("");
		}else{
			pgm_content2.show(""); //show사용할 때 "" 붙여야 원래 css적용 가능함!!!
			VD0204_ul.hide();
		}
	}
};

