	var sigTimeout = null;

	scwin.connect = function() {
		var socket = new SockJS('/websocket');
		stompClient = Stomp.over(socket);

		stompClient.connect({}, scwin.onConnected, scwin.onError);

	}	

	scwin.onConnected = function (frame) {
		console.log('Connected: ' + frame);

	    stompClient.subscribe('/topic/public', scwin.onMessageReceived);

	}

	scwin.onError = function (error) {
		showMessage('Could not connect to WebSocket server. ' + error);
	}			


	scwin.onMessageReceived = function(payload) {
		debugger;
		var jsonObj = JSON.parse(payload.body);

		var attrId = jsonObj.ATTR_ID;
		var dataList = jsonObj.LIST;
		var repRoutItem = dma_search.get("CONTENT1");
		var checkRepRout = 0;
		var routItem = dma_search.get("CONTENT2");
		var routItemString = dma_search.get("CONTENT3");

		//GPS값 튈 경우 아무 동작도 안되게 처리
		if(typeof(jsonObj.GPS_X) != "undefined" && typeof(jsonObj.GPS_Y) != "undefined"){
			if (jsonObj.GPS_X<120 || jsonObj.GPS_Y>130) {
				return;	
			}
			if (jsonObj.GPS_Y<30 || jsonObj.GPS_Y>40) {
				return;	
			}
		}	

		if (repRoutItem != "") {
			//소켓 데이터에 대표노선이 없어 대표노선에 포함된 노선 ID들과 비교			
			for(var i=0; i<dlt_searchitem2.getDataLength(); i++) {
				var routItemOfList = dlt_searchitem2.getCellData(i, "ROUT_ID");		
				if(routItemOfList == jsonObj.ROUT_ID) {
					checkRepRout = 1;	
				}	
			}
			if (checkRepRout == 0){
				return;
			}
		}	

		if (routItem != "") {
			if(routItem != jsonObj.ROUT_ID) {
				return;
			}		
		}

		if (routItemString != "") {

		}			

		var canvas = $("#canvas")[0];
		var ctx = canvas.getContext("2d");	
		var busImg = new Image();
		busImg.src = "/cm/images/tmap/busstop.png";

		//attrId = 2022 : 정류소별 차량 도착 정보
		if(attrId == util.ATTR_ID.BUS_ARRIVAL_INFO){

		//payload.body >>>> {"ATTR_ID":2022,"LIST":[{"ROUT_ID":"RT00000080","REMAIN_TM":1364334,"REMAIN_STTN":2}],"STTN_ID":"ND00000017"}

		//jsonObj.STTN_ID => 정류소아이디
		//jsonObj.LIST => 도착정보 리스트
		// ㄴ ROUT_ID => 노선아이디
		// ㄴ REMAIN_TM => 남은시간(초)
		// ㄴ REMAIN_STTN => 몇 정류소 전

		//	if(attrId == 2022 && nodeId == jsonObj.STTN_ID){
		//		MO0201T0_setTable(dataList);
		//	}			
		}

		//attrId = 4014 : 실시간 차량 위치 정보
		else if(attrId == util.ATTR_ID.BUS_INFO){
				//payload.body >>>> {"GPS_Y":36.484005,"ATTR_ID":4014,"DVC_ID":"DV00000002","GPS_X":127.25973,"VHC_ID":"VH00000002"}		

		//jsonObj.VHC_ID => 차량아이디
		//jsonObj.GPS_X => x좌표
		//jsonObj.GPS_Y; => y좌표

		//content = jsonObj.VHC_ID + "," + jsonObj.GPS_X + ", " + jsonObj.GPS_Y;
			try {
		//		setTimeout(function() {
					var curIndexArr = dlt_BRT_CUR_OPER_INFO.getMatchedIndex("VHC_ID", jsonObj.VHC_ID);

					if(curIndexArr.length != 0) {
						dlt_BRT_CUR_OPER_INFO.setRowJSON(curIndexArr[0], jsonObj, true);

						dataRowJson = dlt_BRT_CUR_OPER_INFO.getDataRow(curIndexArr[0]);
						routMap.moveVehicle("map_MO0201_vehicle", dataRowJson, curIndexArr[0], MO0201G0.getFocusedRowIndex());									

					}
		//		},100);	
			} catch (e) {
				console.log("[scwin.onMessageReceived] Exception :: " + e.message);			
			}	
		}

		//운행 이벤트
		else if (attrId == util.ATTR_ID.BUS_OPER_EVENT) {
			try {
				var eventCode = jsonObj.EVT_CODE;			
				routMap.mapInfo["map_MO0201_vehicle"].isEvent = "on";			
				dlt_BRT_CUR_OPER_INFO.setRowJSON(curIndexArr[0], jsonObj, true);			

				//정류소 도착
				if(eventCode == "ET001") {
					routMap.mapInfo["map_MO0201_vehicle"].divEvent = "ET001";
					busImg.onload = function() {
						x = 40;
						y = 80;
						ctx.drawImage(img, x, y);	
						x += 200;
						ctx.drawImage(img, x, y);
						x += 200;	
						ctx.drawImage(img, x, y);
						x2 = x;
						y2 = y-40;
						ctx.drawImage(img, x2, y2);
						x += 200;	
						ctx.drawImage(img, x, y);
						x += 200;	
						ctx.drawImage(img, x, y);
						x += 200;	
						ctx.drawImage(img, x, y);
						x += 200;	
						ctx.drawImage(img, x, y);
						x += 200;	
						ctx.drawImage(img, x, y);

					}					
				}
				//정류소 출발
				else if(eventCode == "ET002") {
					routMap.mapInfo["map_MO0201_vehicle"].divEvent = "ET002";
				}

				else{
					routMap.mapInfo["map_MO0201_vehicle"].divEvent = "ETC";	
				}

				routMap.showVehicleClickOverlay("map_MO0201_vehicle", dlt_BRT_CUR_OPER_INFO.getAllJSON(),
				dlt_BRT_CUR_OPER_INFO.getCellData(MO0201G0.getFocusedRowIndex(), "VHC_ID"), MO0201G0, "Y", jsonObj.VHC_ID);					
			} catch (e) {
				console.log("[scwin.onMessageReceived] Exception :: " + e.message);					
			}

		}

		//디스패치
		else if (attrId == util.ATTR_ID.DISPATCH) {
			try {
				dlt_BRT_DSPTCH_LOG.insertRow(0);

				var today = new Date();   
				var hours = leadingZeros(today.getHours(), 2);
				var minutes = leadingZeros(today.getMinutes(), 2); 
				var seconds = leadingZeros(today.getSeconds(), 2); 
				var timeResult = hours + ':' + minutes + ':' + seconds
				dlt_BRT_DSPTCH_LOG.setCellData(0, "SEND_DATE", timeResult);
				dlt_BRT_DSPTCH_LOG.setCellData(0, "ROUT_NM", jsonObj.ROUT_NM);
				dlt_BRT_DSPTCH_LOG.setCellData(0, "VHC_NO", jsonObj.VHC_NO);
				dlt_BRT_DSPTCH_LOG.setCellData(0, "DSPTCH_DIV", jsonObj.DSPTCH_DIV);								

				var dsptchDiv = jsonObj.DSPTCH_DIV;
				var dsptchConts = jsonObj.MESSAGE;
				var dsptchKind = jsonObj.DSPTCH_KIND;				
				var contsResult = "";

				var min = "";
				var sec = "";			

				//디스패치가 일반메시지가 아닐경우	
				if(parseInt(dsptchConts) != "undefined" && dsptchDiv != "DP001") {
					if(Math.abs(parseInt(dsptchConts) >= 60)) {
						 min = Math.abs(parseInt(dsptchConts/60)) + "분 ";
					}
					 sec = Math.abs(parseInt(dsptchConts%60)) + "초 ";

					//운행중 디스패치일 경우 
					if(dsptchDiv == "DP002") {
						if(dsptchKind == "DK001") {
							contsResult = "정상 운행중입니다.";
						}
						else if(dsptchKind == "DK002") {
							contsResult = min + sec + "느립니다."; 
						}
						else if(dsptchKind == "DK003") {
							contsResult = min + sec + "빠릅니다."; 
						}
					}

					//정차중 디스패치일 경우
					else if(dsptchDiv == "DP003") {
						contsResult = min + sec + "정차하세요.";	
					}
					dlt_BRT_DSPTCH_LOG.setCellData(0, "DSPTCH_CONTS", contsResult);			
				} 

				//디스패치 메시지가 일반메시지일 경우
				else if(parseInt(dsptchConts) == "undefined" || dsptchDiv == "DP001") {
					dlt_BRT_DSPTCH_LOG.setCellData(0, "DSPTCH_CONTS", jsonObj.MESSAGE);	
				}

				routMap.mapInfo["map_MO0201_vehicle"].isDsptch = "on";
				dlt_BRT_CUR_OPER_INFO.setCellData(curIndexArr[0], "MESSAGE", jsonObj.MESSAGE);			

				var dsptchDiv = jsonObj.DSPTCH_DIV;	

				if (dsptchDiv == "DP001") { //일반메시지
					routMap.mapInfo["map_MO0201_vehicle"].divDsptch = "DP001";
				} else if (dsptchDiv == "DP002") { //운행중디스패치
					routMap.mapInfo["map_MO0201_vehicle"].divDsptch = "DP002";
				} else if (dsptchDiv == "DP003") { //정차중디스패치
					routMap.mapInfo["map_MO0201_vehicle"].divDsptch = "DP003";
				}

				dlt_BRT_CUR_OPER_INFO.setRowJSON(curIndexArr[0], jsonObj, true);					
				routMap.showVehicleClickOverlay("map_MO0201_vehicle", dlt_BRT_CUR_OPER_INFO.getAllJSON(),
				dlt_BRT_CUR_OPER_INFO.getCellData(MO0201G0.getFocusedRowIndex(), "VHC_ID"), MO0201G0, "Y");													

			}

			catch(e) {
				console.log("[scwin.onMessageReceived] Exception :: " + e.message);					
			}
		}


