		$("#map_MO0201").find("li").click( function() {
			var className = this.className;
			var category = $("#map_MO0201").find("#category_map_MO0201");
			var spanId = $(this).find("span");
			var spanClassName = spanId.attr("class");

			var categoryMapId = null;

			//활성화 되어있을 때
			if (className === 'on') {
				currCategory = '';
				this.className = '';

				if (spanClassName == "crossimg") {
					routMap.initNode("map_MO0201_crs");
					routMap.mapInfo["map_MO0201_crs"].isShowCrs = "";
					//routMap.showMarkerListTab("map_MO0201_crs", dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON(), focusIdx, focusGrid);
					routMap.showMarkerListNoGrid("map_MO0201_crs", dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON());

				}
				//정상 정류소
				else if(spanClassName == "normal_busimg") {
					routMap.initNode("map_MO0201_sttn");
					routMap.mapInfo["map_MO0201_sttn"].isShowNormal = "";
					//routMap.showMarkerListTab("map_MO0201_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON(), focusIdx, focusGrid);	
					routMap.showMarkerListNoGrid("map_MO0201_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON());							
				}
				//고장 정류소
				else if(spanClassName == "abnormal_busimg") {
					routMap.initNode("map_MO0201_sttn");
					routMap.mapInfo["map_MO0201_sttn"].isShowAbnormal = "";
					//routMap.showMarkerListTab("map_MO0201_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON(), focusIdx, focusGrid);						
					routMap.showMarkerListNoGrid("map_MO0201_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON());						
				}

			} else {
				this.className = 'on';
				if (spanClassName == "crossimg") {
					routMap.initNode("map_MO0201_crs");
					routMap.mapInfo["map_MO0201_crs"].isShowCrs = "on";
					//routMap.showMarkerListTab("map_MO0201_crs", dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON(), focusIdx, focusGrid);		
					routMap.showMarkerListNoGrid("map_MO0201_crs", dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON());		
				}
				else if(spanClassName == "normal_busimg") {
					routMap.initNode("map_MO0201_sttn");
					routMap.mapInfo["map_MO0201_sttn"].isShowNormal = "on";	
					//routMap.showMarkerListTab("map_MO0201_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON(), focusIdx, focusGrid);
					routMap.showMarkerListNoGrid("map_MO0201_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON());
				}
				else if(spanClassName == "abnormal_busimg") {
					routMap.initNode("map_MO0201_sttn");
					routMap.mapInfo["map_MO0201_sttn"].isShowAbnormal = "on";
					//routMap.showMarkerListTab("map_MO0201_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON(), focusIdx, focusGrid);
					routMap.showMarkerListNoGrid("map_MO0201_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON());
				}

			}	

		}); //end click

	}; //end scwin.sub_selectCategory_submitdone

