scwin.onpageload = function() {

	var autoOptions = {
		Main : { grid : ST0206G0, srchSbm : sub_ST0206G0R0, keyColumn : "NODE_ID", srchGrp : pgm_search, name :  "정류장 목록"}
	 };		

	var mainOptions = {
        EXL:scwin.btn_excel_onclick
		,SEARCH:{}
    };

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.SINGLE_GRID_FORM, autoOptions, mainOptions);
	var mainSrhOptions = {
	};
	com.setMainSrhBtn(wfm_mainSearchBtn, gcm.DISP_TYPE.SINGLE_GRID, autoOptions, mainSrhOptions);

	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}
	var today = year+month+day;
	var firstday = year+month+"01";
	F_DATE.setValue(firstday);
	L_DATE.setValue(today);

	com.executeSubmission(sub_ST0206SHI0);
};

scwin.onpageunload = function() {

};

scwin.btn_excel_onclick = function() {
	var excelOpt = {};  //excel download 옵션 
	excelOpt.fileName = "정류소 정차시간 통계.xls";    //또는 xlsx 확장자 사용 

	pivot_ST0206.pivotExcelDownload(excelOpt);
};

