scwin.sub_ST0206G0R0_submitdone = function(e) {
	com.setFocusedCell(ST0206G0, "chk");
	ST0206G0.setCellChecked(ST0206G0.getFocusedRowIndex(), "chk", true);

	var chkJsonData = ST0206G0.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var nodeId = [];

	com.setFocusedCell(ST0206G0, "REP_ROUT_ID");

	var date = new Date();
	var year = date.getFullYear();
	var month = date.getMonth()+1;
	var day = date.getDate();

	//체크 가져오기
	for(i; i<chkCnt; i++){
		nodeId.push(chkJsonData[i].NODE_ID);
	}

	//조회구분도 넣어서 처리하기. 일별 월별 시간대별
	dma_sub_search.set("SELECT_DIV", SELECT_DIV.getValue());
	dma_sub_search.set("NODE_ID", nodeId);

	com.executeSubmission(sub_ST0206G1R1);
};

