scwin.ST0206G0_oncellclick = function(row,col,colId) {
	var rowCount = dlt_BMS_NODE_MST.getRowCount();

	//chk말고 다른 cell클릭시
	if (col != "0"){
		if(ST0206G0.getCellChecked(row, 'chk')==0){
			for(var i = 0; i < rowCount; i++){
				ST0206G0.setCellChecked(i, 'chk', false);			
			}
		   	ST0206G0.setCellChecked(row, 'chk', true);
		}

	   /*if(ST0206G0.getCellChecked(row, 'chk')==0){
	      ST0206G0.setCellChecked(row, 'chk', true);
	   }else if(ST0206G0.getCellChecked(row, 'chk')==1){
	      ST0206G0.setCellChecked(row, 'chk', false);
	   }*/
	}

	var chkJsonData = ST0206G0.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var nodeId = [];

	//체크된 데이터 가져오기
	for(i; i<chkCnt; i++){
		nodeId.push(chkJsonData[i].NODE_ID);
	}

	dma_sub_search.set("NODE_ID", nodeId);
	dma_sub_search.set("SELECT_DIV", SELECT_DIV.getValue());

	if(chkJsonData.length != 0){
		com.executeSubmission(sub_ST0206G1R1);
		pivot_ST0206.show();
	}else if(chkJsonData.length == 0){
		chart_ST0206.destroy();
		pivot_ST0206.hide();
	}


};

scwin.ST0206G0_onheaderclick = function(chkHeader) {
	var chkJsonData = ST0206G0.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var nodeId = [];

	//체크 가져오기
	for(i; i<chkCnt; i++){
		nodeId.push(chkJsonData[i].NODE_ID);
	}

	dma_sub_search.set("NODE_ID", nodeId);

	if(chkJsonData.length != 0){
		com.executeSubmission(sub_ST0206G1R1);
		pivot_ST0206.show();
	}else if(chkJsonData.length == 0){
		chart_ST0206.destroy();
		pivot_ST0206.hide();
	}
};

scwin.getRandomColor = function(_isAlpha) {
	  var r = getRand(0, 255),
	  g = getRand(0, 255),
	  b = getRand(0, 255),
	  a = getRand(0, 10) / 10;

	  var rgb = _isAlpha ? 'rgba' : 'rgb';
	  rgb += '(' + r + ',' + g + ',' + b;
	  rgb += _isAlpha ? ',' + a + ')' : ')';

	  return rgb;

	  // Return random number from in to max
	  function getRand(min, max) {
	    if (min >= max) return false;
	    return ~~(Math.random() * (max - min + 1)) + min;
	  };
};

var color = ["rgb(226,85,81)","rgb(226,137,85)","rgb(226,207,83)","rgb(174,226,85)","rgb(104,226,85)","rgb(85,85,137)"
				,"rgb(85,226,207)","rgb(85,174,226)","rgb(85,104,226)","rgb(137,85,226)","rgb(207,85,226)","rgb(226,85,151)"
	];

var CharData = function(){
	this.seriesname = {};
	this.fill = {}; //선 아래 여백
	this.backgroundColor = {};
	this.borderColor = {};
	this.data = [];
	this.borderWidth = {}; //선 width
	this.pointBorderWidth = {}; //포인터 width
	this.tension = {};	//곡선 or 직선
	this.pointRadius = {};	//포인트 반지름 0일 경우 포인트 안그려짐
}


var chart_ST0206 = null;

scwin.DrawLineChart_Time = function(chkCnt){
	var datasets = new Array();
	var transData = new Array();
	var i = 0;
	var j = 0;
	var num = 0;

	var rowCount = dlt_BRT_ROUT_STTN_STOP_STAT_2.getRowCount();
	var jsonData = dlt_BRT_ROUT_STTN_STOP_STAT_2.getAllJSON();

	for(i; i<chkCnt; i++){
		var data = new Array();
		var xLabel = new Array();

		for(j; j<rowCount; j++){
			if(jsonData[num].NODE_ID ==  dlt_BRT_ROUT_STTN_STOP_STAT_2.getCellData(j, "NODE_ID")){
				transData.push(dlt_BRT_ROUT_STTN_STOP_STAT_2.getRowJSON(j));
			}else if(jsonData[num].NODE_ID  !=  dlt_BRT_ROUT_STTN_STOP_STAT_2.getCellData(j, "NODE_ID")){
				num = j;
				j = j;
				break;
			}
		}


		for(var y = 0; y < transData.length; y++){
			var label = transData[y].NODE_NM;
			var avrgSpd = transData[y].AVRG_STOP_TIME;
			var statH = transData[y].STAT_H;
			var operDt = transData[y].OPER_DT;

			if(operDt != null){
				var categoryJson = new Object();
				var labelJson = new Object();

				categoryJson = avrgSpd;
				labelJson = statH;

				xLabel.push(labelJson);
				data.push(categoryJson);
			}

		};
	var charData = new CharData();
	var rgb = color[i];

	if(data.length > 0 && xLabel.length > 0){
		charData.data = data;
		charData.label = label;
		charData.borderColor = rgb;
		charData.backgroundColor = rgb;
		charData.borderWidth = 1;
		charData.pointBorderWidth = 1;
		charData.tension = 0;
		charData.pointRadius = 1;

		datasets.push(charData);
	}

	transData = [];
}

	if(chart_ST0206 != null) {
	      chart_ST0206.destroy();   
	}

	var ctx = document.getElementById('chart_ST0206');
	chart_ST0206 = new Chart(ctx, {
		    type: 'line',
			data: {
				//labels:["05시", "06시","07시", "08시","09시", "10시","11시", "12시","13시", "14시","15시", "16시","17시", "18시","19시", "20시","21시", "22시","23시", "24시"],
				labels:xLabel,
				datasets
			  },
		  options: {
		       // responsive: true
		        maintainAspectRatio: false,
		    }
		});

}

scwin.DrawBarChart_Day = function(chkCnt, chkJsonData){
	var datasets = new Array();
	var transData = new Array();
	var i = 0;
	var j = 0;
	var num = 0;

	var rowCount = dlt_BRT_ROUT_STTN_STOP_STAT_2.getRowCount();
	var jsonData = dlt_BRT_ROUT_STTN_STOP_STAT_2.getAllJSON();

	for(i; i<chkCnt; i++){
		var data = new Array();
		var xLabel = new Array();
		var charData = new CharData();

		//charData.label = chkJsonData[i].ROUT_NM;

		for(j; j<rowCount; j++){
			if(jsonData[num].NODE_ID ==  dlt_BRT_ROUT_STTN_STOP_STAT_2.getCellData(j, "NODE_ID")){
				transData.push(dlt_BRT_ROUT_STTN_STOP_STAT_2.getRowJSON(j));
			}else if(jsonData[num].NODE_ID  !=  dlt_BRT_ROUT_STTN_STOP_STAT_2.getCellData(j, "NODE_ID")){
				num = j;
				j = j;
				break;
			}
		}

		for(var y = 0; y < transData.length; y++){
			var label = transData[y].NODE_NM;
			var avrgSpd = transData[y].AVRG_STOP_TIME;
			//var statH = transData[y].STAT_H;
			debugger;
			var operDt = transData[y].OPER_DT;

			if(operDt != null){
				var categoryJson = new Object();
				var labelJson = new Object();

				categoryJson = avrgSpd;
				labelJson = operDt;

				xLabel.push(labelJson);
				data.push(categoryJson);
			}

		};

	var rgb = color[i];

	charData.data = data;
	charData.label = label;
	charData.borderColor = rgb;
	charData.backgroundColor = rgb;
	charData.borderWidth = 1; //선 굵기
	charData.pointBorderWidth = 1; //점 굵기
	charData.tension = 0;
	charData.pointRadius = 1; //점 지름
	charData.maxBarThickness= 60; //막대 굵기

	datasets.push(charData);

	transData = [];
}

	var ctx = document.getElementById('chart_ST0206');
	if(chart_ST0206 != null) {
	      chart_ST0206.destroy();   
	}

	chart_ST0206 = new Chart(ctx, {
		    type: 'bar',
			data: {
				labels:xLabel,
				datasets
			  },
		  options: {
		       // responsive: true
		        maintainAspectRatio: false,
		        scales: {
					yAxes: [{
							ticks: {
								beginAtZero: true,
								userCallback: function(label) {
									if(Math.floor(label) === label) {
									return label;	
									}	
								}	
							}
					}]	
				}
		    }
		});

}

