head.script:

scwin.onpageload = function() {
	var autoOptions = {};				

	 var mainOptions = {
         EXL:scwin.btn_excel_onclick
     }; 

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.SINGLE_GRID, autoOptions, mainOptions);

	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	//글자수 맞추기
	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}

	var weekAgoDay = parseInt(day) - 7;
	var todaysDate = year+month+day;
	var weekAgoDate = year+month+weekAgoDay;
	var firstday = year+month+"01";	

	//F_DATE.setValue(weekAgoDate);
	F_DATE.setValue(todaysDate-1);
	L_DATE.setValue(todaysDate);

	com.executeSubmission(sub_ST0503G0R0);

	//임시 차트 그리기 함수
	scwin.drawLineChart_avg();
	//scwin.drawLineChart_max();
};

scwin.onpageunload = function() {

};

//엑셀 다운로드 클릭 이벤트
scwin.btn_excel_onclick = function() {
	scwin.avgExcel();
	scwin.maxExcel();
};

//평균속도 엑셀
scwin.avgExcel = function(){
	var avgExcelOpt = {};  //excel download 옵션 
	avgExcelOpt.fileName = "주요노선별 유지보수.xls";    //또는 xlsx 확장자 사용 
	pivot_ST0503T0.pivotExcelDownload(avgExcelOpt);
}

//최대속도 엑셀
scwin.maxExcel = function(){
	var maxExcelOpt = {};  //excel download 옵션 
	maxExcelOpt.fileName = "주요노선별 유지보수.xls";    //또는 xlsx 확장자 사용 
	pivot_ST0503T1.pivotExcelDownload(maxExcelOpt);
}

scwin.sub_ST0503G0R0_submitdone = function(e) {
	//chart_ST0503C0.destroy();
	//chart_ST0503C1.destroy();

	scwin.drawLineChart_avg();

	dma_sub_search.set("REP_ROUT_ID", "RR00000002");
	com.executeSubmission(sub_ST0503G1R0);
	//scwin.drawLineChart_max();

};

var colors = ["rgb(226,85,81)","rgb(226,137,85)","rgb(226,207,83)","rgb(174,226,85)","rgb(104,226,85)","rgb(85,85,137)"
				,"rgb(85,226,207)","rgb(85,174,226)","rgb(85,104,226)","rgb(137,85,226)","rgb(207,85,226)","rgb(226,85,151)"
	];

//평균속도 차트 함수
scwin.drawLineChart_avg = function(){
	var xCategories = [];
	var lineData = [];
	var barData = new Array();

	var rowCnt = dlt_BRT_DVC_STAT.getRowCount();
	for(var x = 0; x < rowCnt; x++){
		xCategories[x] = dlt_BRT_DVC_STAT.getCellData(x, "REP_ROUT_NM");

		//바 데이터 정의
		var barJsonData = new Object();
		barJsonData.y = dlt_BRT_DVC_STAT.getCellData(x, "ERROR_CNT");
		barJsonData.name = dlt_BRT_DVC_STAT.getCellData(x, "REP_ROUT_NM");
		barJsonData.repRoutId = dlt_BRT_DVC_STAT.getCellData(x, "REP_ROUT_ID");
		barJsonData.color = colors[x];
		barJsonData = JSON.stringify(barJsonData);

		barData.push(JSON.parse(barJsonData));

		//라인 데이터 정의
		var lineJsonData = new Object();
		lineData[x] = dlt_BRT_DVC_STAT.getCellData(x, "JUDG_CNT");

	}

	var param = com.getParameter();
	var ctx = document.getElementById('mf_tac_layout_contents_'+param.menuCode+'_body_chart_ST0503C0');
	var highChart = new Highcharts.chart(ctx, {
		    title: {
		        text: '주요노선별 차내장치 유지보수 통계'
		    },
		    yAxis: {
		    	title:{
		    		text:'건수'
		    	}
		    },
		    xAxis: {
		        categories: xCategories
		    },

		    credits: {
			        enabled: false //워터마크 제거
			  },
		    /*labels: {
		        items: [{
		            html: '전체 평균속도',
		            style: {
		                left: '50px',
		                top: '1px',
		                color: ( // theme
		                    Highcharts.defaultOptions.title.style &&
		                    Highcharts.defaultOptions.title.style.color
		                ) || 'black'
		            }
		        }]
		    },*/
		    series: [{ //바 그래프
		        type: 'column',
		        name: '장애건수',
		        color:'grey',
		        data: barData,
		        cursor: 'pointer',
	            point: {
	                events: {
	                    click: function () {
	                        //com.alert('Category: ' + this.category + ', value: ' + this.y + ', REP_ROUT_ID: ' + this.repRoutId);
	                        dma_sub_search.set("REP_ROUT_ID", this.repRoutId);
	                        com.executeSubmission(sub_ST0503G1R0);
	                        //scwin.drawLineChart_max(this.repRoutId);
	                    }
	                }
	            }

		    }, {
		        type: 'spline', //라인그래프
		        name: '조치건수',
		        data: lineData,
		        color:'grey',
		        marker: {
		            lineWidth: 2,
		            lineColor: Highcharts.getOptions().colors[3],
		            fillColor: 'white'
		        }
		    }, {
		        type: 'pie', //파이차트
		        name: '장애건수',
		        data: barData,
		        center: [70, 10],
		        size: 70,
		        showInLegend: false,
		        dataLabels: {
		            enabled: false
		        }
		    }]
		});
}

scwin.sub_ST0503G1R0_submitdone = function(){
	scwin.drawLineChart_max();
}

