scwin.onpageload = function() {

	var autoOptions = {
		Main : { grid : ST0805G0, srchSbm : sub_ST0805G0R0, keyColumn : "OPER_DT", srchGrp : pgm_header, name :  "노선 목록"},
		Sub1 : { grid : ST0805G1, srchSbm : sub_ST0805G1R0, keyColumn : "NODE_ID", srchGrp : pgm_header, name :  "정류소 목록"}
	 };		

	  var mainOptions = {
         EXL:scwin.btn_excel_onclick
		, SEARCH : {}
      };

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID_FORM, autoOptions, mainOptions);
	var mainSrhOptions = {
	};	
	com.setMainSrhBtn(wfm_mainSearchBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions, mainSrhOptions);

	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}

	var today = year+month+day;
	var firstday = year+month+"01";

	F_DATE.setValue(firstday);
	L_DATE.setValue(today);
	ED_STAT_H.setValue(25);
	dma_sub_search.set("TAB_DIV", "DELAY_TIME");

	com.executeSubmission(sub_ST0805SHI0);
	com.executeSubmission(sub_selectStatHItem);	
};

