scwin.sub_ST0805G1R0_submitdone = function(e) {
	com.setFocusedCell(ST0805G1, "chk");
	ST0805G1.setCellChecked(ST0805G1.getFocusedRowIndex(), "chk", true);

	var chkJsonData = ST0805G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var nodeId = [];

	com.setFocusedCell(ST0805G1, "NODE_ID");

	var date = new Date();
	var year = date.getFullYear();
	var month = date.getMonth()+1;
	var day = date.getDate();

	//체크 가져오기
	for(i; i<chkCnt; i++){
		nodeId.push(chkJsonData[i].NODE_ID);
	}

	//조회구분도 넣어서 처리하기. 일별 월별 시간대별
	dma_sub_search.set("SELECT_DIV", SELECT_DIV.getValue());
	dma_sub_search.set("NODE_ID", nodeId);
	com.executeSubmission(sub_ST0805G1R1);

};

scwin.ST0805G1_oncellclick = function(row,col,colId) {
	if (col != "0"){
	   if(ST0805G1.getCellChecked(row, 'chk')==0){
	      ST0805G1.setCellChecked(row, 'chk', true);
	   }else if(ST0805G1.getCellChecked(row, 'chk')==1){
	      ST0805G1.setCellChecked(row, 'chk', false);
	   }
	}

	var chkJsonData = ST0805G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var nodeId = [];

	//체크된 데이터 가져오기
	for(i; i<chkCnt; i++){
		nodeId.push(chkJsonData[i].NODE_ID);
	}

	dma_sub_search.set("NODE_ID", nodeId);
	dma_sub_search.set("SELECT_DIV", SELECT_DIV.getValue());

	if(chkJsonData.length != 0){
		com.executeSubmission(sub_ST0805G1R1);
		pivot_ST0805.show();
	}else if(chkJsonData.length == 0){
		chart_ST0805.destroy();
		pivot_ST0805.hide();
	}


};

scwin.ST0805G1_onheaderclick = function(chkHeader) {
	var chkJsonData = ST0805G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var nodeId = [];

	//체크 가져오기
	for(i; i<chkCnt; i++){
		nodeId.push(chkJsonData[i].NODE_ID);
	}

	dma_sub_search.set("NODE_ID", nodeId);

	if(chkJsonData.length != 0){
		com.executeSubmission(sub_ST0805G1R1);
		pivot_ST0805.show();
	}else if(chkJsonData.length == 0){
		chart_ST0805.destroy();
		pivot_ST0805.hide();
	}
};

scwin.getRandomColor = function(_isAlpha) {
	  var r = getRand(0, 255),
	  g = getRand(0, 255),
	  b = getRand(0, 255),
	  a = getRand(0, 10) / 10;

	  var rgb = _isAlpha ? 'rgba' : 'rgb';
	  rgb += '(' + r + ',' + g + ',' + b;
	  rgb += _isAlpha ? ',' + a + ')' : ')';

	  return rgb;

	  // Return random number from in to max
	  function getRand(min, max) {
	    if (min >= max) return false;
	    return ~~(Math.random() * (max - min + 1)) + min;
	  };
};


var CharData = function(){
	this.seriesname = {};
	this.fill = {}; //선 아래 여백
	this.backgroundColor = {};
	this.borderColor = {};
	this.data = [];
	this.borderWidth = {}; //선 width
	this.pointBorderWidth = {}; //포인터 width
	this.tension = {};	//곡선 or 직선
	this.pointRadius = {};	//포인트 반지름 0일 경우 포인트 안그려짐
}


var chart_ST0805 = null;

scwin.DrawLineChart_Time = function(chkCnt, chkJsonData){
	var datasets = new Array();
	var transData = new Array();
	var i = 0;
	var j = 0;
	var num = 0;

	var rowCount = dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getRowCount();
	var jsonData = dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getAllJSON();

	for(i; i<chkCnt; i++){
		var data = new Array();
		var xLabel = new Array();

		for(j; j<rowCount; j++){
			if(jsonData[num].NODE_ID ==  dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getCellData(j, "NODE_ID")){
				transData.push(dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getRowJSON(j));
			}else if(jsonData[num].NODE_ID  !=  dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getCellData(j, "NODE_ID")){
				num = j;
				j = j;
				break;
			}
		}

		for(var y = 0; y < transData.length; y++){
			var label = transData[y].STTN_NM;

			var val = transData[y].DELAY_TIME;

			var statH = transData[y].STAT_H;
			var operDt = transData[y].OPER_DT;

			if(operDt != null){
				var categoryJson = new Object();
				var labelJson = new Object();

				categoryJson = val;
				labelJson = statH;

				xLabel.push(labelJson);
				data.push(categoryJson);
			}

		};
	var charData = new CharData();
	var rgb = scwin.getRandomColor();

	charData.data = data;
	charData.label = label;
	charData.borderColor = rgb;
	charData.backgroundColor = rgb;
	charData.borderWidth = 1;
	charData.pointBorderWidth = 1;
	charData.tension = 0;
	charData.pointRadius = 1;


	datasets.push(charData);

	transData = [];
}

	if(chart_ST0805 != null) {
	      chart_ST0805.destroy();   
	}

	var ctx = document.getElementById('chart_ST0805');
	chart_ST0805 = new Chart(ctx, {
		    type: 'bar',
			data: {
				labels:xLabel,
				datasets
			  },
		  options: {
		       // responsive: true
		        maintainAspectRatio: false,

		    }
		});

}

scwin.DrawBarChart_Week = function(chkCnt, chkJsonData){
	var datasets = new Array();
	var transData = new Array();
	var i = 0;
	var j = 0;
	var num = 0;

	var rowCount = dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getRowCount();
	var jsonData = dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getAllJSON();

	for(i; i<chkCnt; i++){
		var data = new Array();
		var xLabel = new Array();
		var charData = new CharData();

		//charData.label = chkJsonData[i].ROUT_NM;

		for(j; j<rowCount; j++){
			if(jsonData[num].NODE_ID ==  dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getCellData(j, "NODE_ID")){
				transData.push(dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getRowJSON(j));
			}else if(jsonData[num].NODE_ID  !=  dlt_BRT_STTN_PNCTLTY_STAT_PIVOT_2.getCellData(j, "NODE_ID")){
				num = j;
				j = j;
				break;
			}
		}

		for(var y = 0; y < transData.length; y++){
			var label = transData[y].STTN_NM;

			var val = transData[y].DELAY_TIME;

			//var statH = transData[y].STAT_H;
			var operDt = transData[y].DAY_WEEK;
			var weekKo = ['월요일', '화요일', '수요일', '목요일', '금요일', '토요일', '일요일']
			var weekH = ''
			for(var x in weekKo){
				if(operDt == x){
					var weekH = weekKo[x];
				}
			}

			if(operDt != null){
				var categoryJson = new Object();
				var labelJson = new Object();

				categoryJson = val;
				labelJson = weekH;

				xLabel.push(labelJson);
				data.push(categoryJson);
			}

		};

	var rgb = scwin.getRandomColor();

	charData.data = data;
	charData.label = label;
	charData.borderColor = rgb;
	charData.backgroundColor = rgb;
	charData.borderWidth = 1; //선 굵기
	charData.pointBorderWidth = 1; //점 굵기
	charData.tension = 0;
	charData.pointRadius = 1; //점 지름
	charData.maxBarThickness= 60; //막대 굵기

	datasets.push(charData);

	transData = [];
}

	var ctx = document.getElementById('chart_ST0805');
	if(chart_ST0805 != null) {
	      chart_ST0805.destroy();   
	}

	chart_ST0805 = new Chart(ctx, {
		    type: 'bar',
			data: {
				labels:xLabel,
				datasets
			  },
		  options: {
		       // responsive: true
		        maintainAspectRatio: false,
		        scales: {
					yAxes: [{
							ticks: {
								beginAtZero: true,
								userCallback: function(label) {
									if(Math.floor(label) === label) {
									return label;	
									}	
								}	
							}
					}]
				}
		    }
		});

}

