scwin.ST_STAT_H_onviewchange = function(info) {
	var stStath = ST_STAT_H.getValue();
	var edStath = ED_STAT_H.getValue();

	if (stStath > edStath && edStath != "") {
		com.messagBox("alert", "지정할 수 없는 시간입니다.", scwin.checkCloseST(info));
	}
	com.executeSubmission(sub_ST0805G1R1);
};

scwin.checkCloseST = function(info) {
	ST_STAT_H.setValue(info.oldValue);
}

scwin.ED_STAT_H_onviewchange = function(info) {
	var stStath = ST_STAT_H.getValue();
	var edStath = ED_STAT_H.getValue();

	if (stStath > edStath && edStath != "") {
		com.messagBox("alert", "지정할 수 없는 시간입니다.", scwin.checkCloseED(info));
	}
	com.executeSubmission(sub_ST0805G1R1);
};

scwin.checkCloseED = function(info) {
	ED_STAT_H.setValue(info.oldValue);
}

scwin.ST0805T1_onrefresh = function(info) {
	if(SELECT_DIV.getValue() == 'WEEK'){
		var setWeek = document.querySelectorAll('.pvtColLabel');
		var weekKo = ['월요일', '화요일', '수요일', '목요일', '금요일', '토요일', '일요일']
		var weekH = ''
		for(var y = 0; y < setWeek.length; y++){
			for(var x in weekKo){
				var transweek = setWeek[y].textContent;
				if(Number(transweek) == x){
					setWeek[y].innerText = weekKo[x];
				}
			}
		}
	}
}

scwin.checkCloseF = function(info) {
	F_DATE.setValue(info.oldValue);
}

scwin.F_DATE_onviewchange = function(info) {
	var ldate = L_DATE.getValue();
	var fdate = F_DATE.getValue();

