scwin.SELECT_DIV_onviewchange = function(info) {
	dma_sub_search.set("SELECT_DIV", info.newValue);

	if(info.newValue == 'TIME'){
		TIME_STAT_BOX.show();
	}

	else if(info.newValue == 'MONTH'){
		F_DATE.setCalendarValueType("yearMonth");
		L_DATE.setCalendarValueType("yearMonth");
		TIME_STAT_BOX.hide();
	}

	else{
		F_DATE.setCalendarValueType("yearMonthDate");
		L_DATE.setCalendarValueType("yearMonthDate");
		TIME_STAT_BOX.hide();
	}
	com.executeSubmission(sub_ST0805G1R1);
};

scwin.sub_ST0805G1R1_submitdone = function(e) {
	com.executeSubmission(sub_ST0805G2R0);
};

scwin.sub_ST0805G2R0_submitdone = function(e) {
	var chkJsonData = ST0805G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	chart_ST0805.destroy();

	if(chkCnt > 0 && SELECT_DIV.getValue() == 'TIME'){
		ST0805T0.show();
		ST0805T1.hide();
		scwin.DrawLineChart_Time(chkCnt, chkJsonData);
	}else if(chkCnt > 0 && SELECT_DIV.getValue() == 'WEEK'){
		ST0805T1.show();
		ST0805T0.hide();
		scwin.DrawBarChart_Week(chkCnt, chkJsonData);
	}else if(chkCnt > 0 && SELECT_DIV.getValue() == 'MONTH'){
		ST0805T1.show();
		ST0805T0.hide();
		scwin.DrawBarChart_Month(chkCnt);
	}
};
