scwin.onpageload = function() {
	 var autoOptions = {
		Main : { grid : VH0605G0, srchSbm : sub_VH0605G0R0, keyColumn : "UPD_DTM", srchGrp : pgm_header, name : "교차로신호 운영 및 교통정보 이력",
					focusColumn : "UPD_DTM"}
	 };

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.SINGLE_GRID, autoOptions);

	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	//글자수 맞추기
	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}

	var weekAgoDay = parseInt(day) - 7;
	var todaysDate = year+month+day;
	var weekAgoDate = year+month+weekAgoDay;
	var firstday = year+month+"01";	

	//F_DATE.setValue(weekAgoDate);
	F_DATE.setValue(todaysDate);
	L_DATE.setValue(todaysDate);

	com.executeSubmission(sub_grpCrsItem);
	com.executeSubmission(sub_crsItem);

};	

scwin.onpageunload = function() {

}

scwin.VH0605G0_onrowindexchange = function(row,oldRow) {

};

scwin.sub_VH0605G0R0_submitdone = function(e) {
	com.setFocusedCell(VH0605G0, "UPD_DTM");

	for (i=0; i<dlt_BMS_CRS_SIGOPER_DTL_HIS.getDataLength(); i++) {
		var periodCntr = "";
		var periodCntrMin = "";
		var periodCntrSec = "";	
		var prvPeriodLen = "";
		var prvPeriodLenMin = "";
		var prvPeriodLenSec = "";
		var periodLen = "";
		var periodLenMin = "";
		var periodLenSec = "";

		var pwrSts = "";
		var mcuSts = "";
		var dimmingSts = "";

		periodCntr = dlt_BMS_CRS_SIGOPER_DTL_HIS.getCellData(i, "PERIOD_CNTR");
		prvPeriodLen = dlt_BMS_CRS_SIGOPER_DTL_HIS.getCellData(i, "PREV_PERIOD_LEN");
		periodLen = dlt_BMS_CRS_SIGOPER_DTL_HIS.getCellData(i, "PERIOD_LEN");

		if((parseInt(periodCntr) >= 60)) {
			periodCntrMin = parseInt(periodCntr/60) + "분 ";
		}

		periodCntrSec = parseInt(periodCntr%60) + "초";
		periodCntr = periodCntrMin + periodCntrSec;

		if((parseInt(prvPeriodLen) >= 60)) {
			prvPeriodLenMin = parseInt(prvPeriodLen/60) + "분 ";
		}

		prvPeriodLenSec = parseInt(prvPeriodLen%60) + "초";
		prvPeriodLen = prvPeriodLenMin + prvPeriodLenSec;

		if((parseInt(periodLen) >= 60)) {
			periodLenMin = parseInt(periodLen/60) + "분 ";
		}

		periodLenSec = parseInt(periodLen%60) + "초";
		periodLen = periodLenMin + periodLenSec;

		dlt_BMS_CRS_SIGOPER_DTL_HIS.setCellData(i, "PERIOD_CNTR", periodCntr);		 	
		dlt_BMS_CRS_SIGOPER_DTL_HIS.setCellData(i, "PREV_PERIOD_LEN", prvPeriodLen);		
		dlt_BMS_CRS_SIGOPER_DTL_HIS.setCellData(i, "PERIOD_LEN", periodLen);		


		crsSts = dlt_BMS_CRS_SIGOPER_DTL_HIS.getCellData(i, "PWR_STS");
		mcuSts = dlt_BMS_CRS_SIGOPER_DTL_HIS.getCellData(i, "MCU_STS");
		dimmingSts = dlt_BMS_CRS_SIGOPER_DTL_HIS.getCellData(i, "DIMMING_STS");

		switch(crsSts) {
			//정상
			case "0":
			VH0605G0.setCellClass(i, "PWR_STS", "cond_normal");
			break;

			//Power Fail
			case "1":
			VH0605G0.setCellClass(i, "PWR_STS", "cond_abnormal");
			break;
		}

		switch(mcuSts) {
			//정상
			case "0":
			VH0605G0.setCellClass(i, "MCU_STS", "cond_normal");
			break;

			//Offline
			case "1":
			VH0605G0.setCellClass(i, "MCU_STS", "cond_abnormal");
			break;
		}

		switch(dimmingSts) {
			//정상
			case "0":
			VH0605G0.setCellClass(i, "DIMMING_STS", "cond_normal");
			break;

			//조광제어중
			case "1":
			VH0605G0.setCellClass(i, "DIMMING_STS", "cond_neutrality");
			break;
		}
	}
};

scwin.F_DATE_onviewchange = function(info) {
	var ldate = L_DATE.getValue();
	var fdate = F_DATE.getValue();

	if (fdate > ldate && ldate != ""){
		com.messagBox("alert", "지정할 수 없는 날짜입니다.",scwin.checkCLoseF(info));
	}

	else{
		calendar_radio.setValue("DIRECT");
	}

};
scwin.checkCLoseF = function(info) {
	F_DATE.setValue(info.oldValue);
};

scwin.checkCLoseL = function(info) {
	L_DATE.setValue(info.oldValue);
};	

scwin.L_DATE_onviewchange = function(info) {
	var ldate = L_DATE.getValue();
	var fdate = F_DATE.getValue();

	if (fdate > ldate){
		com.messagBox("alert", "지정할 수 없는 날짜입니다.",scwin.checkCLoseL(info));
	}

	else{
		calendar_radio.setValue("DIRECT");
	}		
};

//라디오 버튼에 따라 날짜 설정
scwin.calendar_radio_onviewchange = function(info) {
	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	//글자수 맞추기
	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}

	var weekAgoDay = parseInt(day) - 7;
	var todaysDate = year+month+day;
	var weekAgoDate = year+month+weekAgoDay;
	var firstday = year+month+"01";		

	if(parseInt(weekAgoDay) < 0) {
		weekAgoDate = firstday;	
	}			

	if(info.value == "DIRECT") {
		F_DATE.setValue();
		L_DATE.setValue();
	}

	else if(info.value == "TODAY") {
		F_DATE.setValue(todaysDate);
		L_DATE.setValue(todaysDate);	
	}

	else if(info.value == "WEEK") {
		F_DATE.setValue(weekAgoDate);
		L_DATE.setValue(todaysDate);			
	}

	else if(info.value == "MONTH") {
		F_DATE.setValue(firstday);
		L_DATE.setValue(todaysDate);				
	}
};

scwin.getFooterDivTop = function(data, dataType) {
	var result = scwin.getFooterCount(data, dataType, "top", "PWR_STS");
	return result;
};

scwin.getFooterDivMiddle = function(data, dataType) {
	var result = scwin.getFooterCount(data, dataType, "middle", "MCU_STS");
	return result;
};

scwin.getFooterDivBottom = function(data, dataType) {
	var result = scwin.getFooterCount(data, dataType, "bottom", "DIMMING_STS");
	return result;
};	

scwin.getFooterCount = function(data, dataType, footerPosition, dataColumn) {
	var sum = 0;

	var stsCount01 = 0;
	var stsCount02 = 0;

	var stsCount03 = 0;
	var stsCount04 = 0;

	var stsCount05 = 0;
	var stsCount06 = 0;

	var result = "";

	if (data.length > 0) {
		sum = data.length;

		for (var i=0; i<data.length; i++) {


				if (dataColumn == "PWR_STS") {
					switch(data[i]) {
						//정상
						case "0":
						stsCount01++;
						break;

						//Power Fail
						case "1":
						stsCount02++;
						break;								
					}

				}

				else if (dataColumn == "MCU_STS") {
					switch(data[i]) {
						//정상
						case "0":
						stsCount03++;
						break;

						//Offline
						case "1":
						stsCount04++;
						break;								
					}						
				}

				else if (dataColumn == "DIMMING_STS") {
					switch(data[i]) {
						//정상
						case "0":
						stsCount05++;
						break;

						//조광제어중
						case "1":
						stsCount06++;
						break;								
					}						
				}

		}				


		if(footerPosition == 'top') {
			result = "총계 : " + sum + "건, 전원상태 - 정상 : " + stsCount01+ "건, Power Fail : " + stsCount02 + "건";						
		}

		else if (footerPosition == 'middle') {
			result =  "MCU상태 - 정상 : " + stsCount03 + "건, Offline : " + stsCount04 + "건";		
		}

		else if (footerPosition == 'bottom') {
			result =  "조광제어상태 - 정상 : " + stsCount05 + "건, 조광제어중 : " + stsCount06 + "건";	
		}

		return result;
	}
}			


scwin.sub_crsItem_submitdone = function(e) {
	com.searchGrid(VH0605G0, sub_VH0605G0R0);
};
